INSERT INTO `birthday_login_bonus_drama_script` VALUES (/*command_id*/1110501040, /*drama_id*/1110501, /*command_type*/11, /*param_01*/'111011', /*param_02*/'哇啊啊啊啊啊啊，这蜡烛是怎么回事！？\n在求知欲的驱使下忍不住吹灭了，\n这可疑的香味真让人感兴趣！\n知识通过鼻腔进入体内了！', /*param_03*/'5', /*param_04*/'1', /*param_05*/'1', /*param_06*/'0', /*param_07*/'0', /*param_08*/'');
