INSERT INTO `birthday_login_bonus_drama_script` VALUES (/*command_id*/1110501043, /*drama_id*/1110501, /*command_type*/11, /*param_01*/'111011', /*param_02*/'这个蜡烛难道是你做的吗？\n什么，精油里含有那么稀有的成分吗？\n是特意为了给我庆祝生日？\n后辈……你真是！', /*param_03*/'4', /*param_04*/'1', /*param_05*/'1', /*param_06*/'0', /*param_07*/'0', /*param_08*/'');
