INSERT INTO `caravan_dish` VALUES (/*dish_id*/52001, /*recipe_id*/51001, /*name*/'佩可莉姆的饱腹双份饭团', /*new_line_name*/'佩可莉姆的\n饱腹双份饭团', /*description*/'佩可莉姆精心制作的特大饭团。\n这么大似乎是为了体现出吃饭是活力来源。\n将最高级的食材包入让一般人饱腹的两倍分量中，\n是一道很有【美食殿堂】特色的菜。', /*effect_description*/'骰子增加至两个。按照掷出的总数前进。', /*sub_effect_description*/'获得500里程。', /*sold_price*/500, /*category*/1, /*effect_type*/1, /*effect_value*/2, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/1, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/2);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52002, /*recipe_id*/51002, /*name*/'可可萝的悠闲小憩沙拉', /*new_line_name*/'可可萝的\n悠闲小憩沙拉', /*description*/'可可萝为大家准备的沙拉。\n既考虑到营养均衡，还能吃饱。\n不仅注重味道，还保证了外观。\n是一道能带给大家对冒险的活力，充满慈爱的菜品。', /*effect_description*/'下一次投掷骰子的结果必定是“1”。同时会跳过对下一个回合的计数。', /*sub_effect_description*/'获得500里程。', /*sold_price*/500, /*category*/1, /*effect_type*/3, /*effect_value*/30001, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/2, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/2);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52003, /*recipe_id*/51003, /*name*/'凯露的极品五星汉堡', /*new_line_name*/'凯露的\n极品五星汉堡', /*description*/'夹着凯露想吃的食物的汉堡。\n香喷喷的面包和肉香味刺激着食欲。\n它是由非魔物的食材制成的。\n凯露认为这道菜对于冒险食品来说过于豪华了。', /*effect_description*/'下次投掷骰子的结果必定是“5”。', /*sub_effect_description*/'获得1个平凡的未鉴定宝藏。', /*sold_price*/500, /*category*/1, /*effect_type*/2, /*effect_value*/20004, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/1, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/1);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52004, /*recipe_id*/51004, /*name*/'莉莉的脚踏实地进步柏饼', /*new_line_name*/'莉莉的\n脚踏实地进步柏饼', /*description*/'是从莉莉制作的柏饼中……精心筛选的\n完美成品。不仅使用了最好的食材，\n还因为制作失败的那部分都被莉莉吃了，\n所以光从单价来看，就已经是很奢侈的菜品了。', /*effect_description*/'接下来三次投掷骰子的结果都必定为“4”。', /*sub_effect_description*/'获得800里程。', /*sold_price*/500, /*category*/1, /*effect_type*/2, /*effect_value*/20001, /*effect_turn*/3, /*effect_times*/0, /*prefab_id*/3, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/4);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52005, /*recipe_id*/51005, /*name*/'可璃亚的多彩丰盛豪华便当', /*new_line_name*/'可璃亚的\n多彩丰盛豪华便当', /*description*/'可璃亚一大早就开始准备的在冒险时食用的便当。\n菜色种类繁多，每样都堪称绝品。\n因为里面的菜品对大家保密了，\n所以令人很期待打开盖子的那一刻。', /*effect_description*/'触发随机事件。', /*sub_effect_description*/'下次投掷骰子时，会多走一个格子。', /*sold_price*/500, /*category*/3, /*effect_type*/4, /*effect_value*/1, /*effect_turn*/0, /*effect_times*/0, /*prefab_id*/3, /*disable_category*/0, /*sub_effect_type*/11, /*sub_effect_value*/1);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52006, /*recipe_id*/51006, /*name*/'普雷西娅的贪吃鬼香喷喷肉包', /*new_line_name*/'普雷西娅的贪吃鬼\n香喷喷肉包', /*description*/'塞满了普雷西娅精心挑选的食材的肉包。\n按照她的要求，做得比普通肉包\n大了好几倍。味道和分量都是满分，绝对会让人\n吃得很开心♪担任烹饪的可璃亚是这么说的。', /*effect_description*/'下次投掷骰子的结果会是“4、5、6”其中的一个。', /*sub_effect_description*/'获得800里程。', /*sold_price*/500, /*category*/1, /*effect_type*/2, /*effect_value*/20002, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/1, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/4);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52007, /*recipe_id*/51007, /*name*/'嘉夜和祈梨的食欲倍增烤肉三明治', /*new_line_name*/'嘉夜和祈梨的\n食欲倍增烤肉三明治', /*description*/'嘉夜和祈梨作为便当带来的烤肉。\n体现出祈梨常去的餐厅的硬派风格，作为黑帮\n能拿出三倍干劲，两人配合就是六倍干劲。\n能爆发出下克上的力量……好像是这样的。', /*effect_description*/'下一次投掷骰子的结果必定是“3”，再下一次投掷骰子的结果必定是“6”。', /*sub_effect_description*/'获得一张抽奖券。', /*sold_price*/500, /*category*/1, /*effect_type*/7, /*effect_value*/20003, /*effect_turn*/2, /*effect_times*/0, /*prefab_id*/1, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/3);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52008, /*recipe_id*/51008, /*name*/'珠希的招财彩虹鲷鱼烧', /*new_line_name*/'珠希的招财\n彩虹鲷鱼烧', /*description*/'珠希他们将带入“吉奥·特尔哥尼亚”的\n鲷鱼烧概念融合了当地文化后，\n以神奇的方式改造出来的甜食。散发着彩虹般的光芒，据说\n比兰德索尔的鲷鱼烧还要甜数倍。', /*effect_description*/'下次停在里程格子上时，里程获得量提升200%。', /*sub_effect_description*/'获得500里程。', /*sold_price*/500, /*category*/2, /*effect_type*/5, /*effect_value*/200, /*effect_turn*/0, /*effect_times*/1, /*prefab_id*/2, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/2);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52009, /*recipe_id*/51009, /*name*/'克萝茜的满功率土豆沙拉', /*new_line_name*/'克萝茜的\n满功率土豆沙拉', /*description*/'克萝茜拼尽全力制作的土豆沙拉。\n本来是打算做成脆皮蛋糕的，\n因为不小心捏碎，就改变了做法，最终完成了这道菜品。\n先不管来历如何，总之非常美味。', /*effect_description*/'[FF7C4E,D62146]【不可覆盖】[-]下一次投掷骰子的结果必定是“8”，再下一次投掷骰子的结果必定是“1”。', /*sub_effect_description*/'获得1个平凡的未鉴定宝藏。', /*sold_price*/500, /*category*/1, /*effect_type*/7, /*effect_value*/1, /*effect_turn*/2, /*effect_times*/0, /*prefab_id*/2, /*disable_category*/1, /*sub_effect_type*/10, /*sub_effect_value*/1);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52010, /*recipe_id*/51010, /*name*/'绫音和胡桃的蓝天郊游套餐', /*new_line_name*/'绫音和胡桃的\n蓝天郊游套餐', /*description*/'绫音和胡桃两人一起准备的午餐篮。\n里面装满了各式美味佳肴，体现出与同伴\n去冒险的愉快心情，能够让途中遇见的各种事\n都变得像是更加特别的经历。', /*effect_description*/'地图上的“里程格子”、“宝藏格子” 、“商店格子”的品级上升一级。效果持续到停在对应格子两次为止。', /*sub_effect_description*/'获得500里程。', /*sold_price*/500, /*category*/2, /*effect_type*/6, /*effect_value*/1, /*effect_turn*/0, /*effect_times*/2, /*prefab_id*/3, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/2);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52011, /*recipe_id*/51011, /*name*/'莉玛的不可思议甜苹果', /*new_line_name*/'莉玛的\n不可思议甜苹果', /*description*/'莉玛用似似花给的苹果制作的秘密\n甜食。清爽的酸味和浓郁的甜味\n仿佛魔法一般相辅相成，达成了奇妙的平衡。\n能让食用的人瞬间为之着迷。', /*effect_description*/'下一次投掷骰子的结果必定是“2”，再下一次投掷骰子的结果必定是“7”。', /*sub_effect_description*/'获得一张抽奖券。', /*sold_price*/500, /*category*/1, /*effect_type*/7, /*effect_value*/1, /*effect_turn*/2, /*effect_times*/0, /*prefab_id*/3, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/3);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52012, /*recipe_id*/51012, /*name*/'妮侬的独创一二三寿司', /*new_line_name*/'妮侬的独创\n一二三寿司', /*description*/'妮侬带到吉奥·特尔哥尼亚的特别\n寿司。虽然是采用了严格挑选的新鲜海鲜制成的杰作。\n但部分食材脱离了一般寿司的理念，\n是按照妮侬自己的方式制作的。', /*effect_description*/'下次投掷骰子的结果会是“1、2、3”其中的一个。并且跳过对下一个回合的计数。', /*sub_effect_description*/'获得1个平凡的未鉴定宝藏。', /*sold_price*/500, /*category*/1, /*effect_type*/3, /*effect_value*/30002, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/2, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/1);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52013, /*recipe_id*/51013, /*name*/'克蕾琪塔的开运点心拼盘', /*new_line_name*/'克蕾琪塔的\n开运点心拼盘', /*description*/'克蕾琪塔发明的，在吉奥·特尔哥尼亚流通的\n巧克力钱币的拼盘。粗略一看\n可能会被当成吃金币的笨蛋，\n但它隐藏着吃货币这种违反道德的刺激感。', /*effect_description*/'开启品级为2的里程商店。', /*sub_effect_description*/'获得800里程。', /*sold_price*/500, /*category*/3, /*effect_type*/8, /*effect_value*/2, /*effect_turn*/0, /*effect_times*/0, /*prefab_id*/3, /*disable_category*/0, /*sub_effect_type*/10, /*sub_effect_value*/4);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52014, /*recipe_id*/51014, /*name*/'璃乃的“时间就是金时”刨冰', /*new_line_name*/'璃乃的\n“时间就是金时”刨冰', /*description*/'可爱的妹妹为哥哥精心制作的特别刨冰。\n据说只要吃一口，冰凉的\n甜味就能让人精神振奋，\n成为不会放过任何优惠的贤惠母亲。', /*effect_description*/'移动时会跳过里程格子。效果持续两回合。', /*sub_effect_description*/'下次投掷骰子时，会多走一个格子。', /*sold_price*/500, /*category*/1, /*effect_type*/9, /*effect_value*/1, /*effect_turn*/2, /*effect_times*/0, /*prefab_id*/2, /*disable_category*/0, /*sub_effect_type*/11, /*sub_effect_value*/1);
INSERT INTO `caravan_dish` VALUES (/*dish_id*/52015, /*recipe_id*/51015, /*name*/'静流的爱意满满汉堡肉', /*new_line_name*/'静流的\n爱意满满汉堡肉', /*description*/'姐姐为弟弟精心制作的\n特制汉堡肉。热腾腾的肉汁\n与满满的爱意融合在一起，美味到\n只吃一口就能令人兴奋地奔跑起来。', /*effect_description*/'骰子增加至两个。按照掷出的总数前进。', /*sub_effect_description*/'下次投掷骰子时，会多走一个格子。', /*sold_price*/500, /*category*/1, /*effect_type*/1, /*effect_value*/2, /*effect_turn*/1, /*effect_times*/0, /*prefab_id*/1, /*disable_category*/0, /*sub_effect_type*/11, /*sub_effect_value*/1);
