INSERT INTO `cgg_gacha_info` VALUES (/*gacha_type*/1, /*cgg_id*/1, /*gacha_name*/'常规藏品', /*gacha_description*/'常规藏品有大量海报、三角旗等\n可以挂在房间内做装饰的经典周边！\n用慈乐之音的周边装点你的房间吧！', /*cost_currency_num*/3, /*gacha_intro*/'1次{0}个奖章！\n用慈乐之音的经典周边装点你的房间吧！');
INSERT INTO `cgg_gacha_info` VALUES (/*gacha_type*/2, /*cgg_id*/1, /*gacha_name*/'高级藏品', /*gacha_description*/'高级藏品有大量签名纸、照片等\n★★以上的周边！\n收集大量稀有周边的好机会！', /*cost_currency_num*/10, /*gacha_intro*/'1次{0}个奖章！\n一定可以获得成员亲笔签名纸等★★以上的周边！');
INSERT INTO `cgg_gacha_info` VALUES (/*gacha_type*/3, /*cgg_id*/1, /*gacha_name*/'庆典藏品', /*gacha_description*/'庆典藏品都是在慈乐之音夏日演唱会中\n大显神威的各种周边！\n带上获得的周边前往演唱会会场吧！', /*cost_currency_num*/3, /*gacha_intro*/'1次{0}个奖章！\n来收集参加慈乐之音演唱会的必备道具吧！');
