INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1001, /*name*/'春咲日和莉', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是日和莉♪\n我会精神百倍地加油的♪', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'哦！\n那个人好像很困扰！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'喂喂，骑士君\n我们去帮帮忙吧~');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1002, /*name*/'草野优衣', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我必须要像大家那样\n变得更强才行', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我想强大到\n足以守护最重要的人……啊', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我、我、\n都在说些什么呀\n啊哈哈……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1003, /*name*/'士条怜', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'试着练习击剑吧？', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'应该可以稍微锻炼一下', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'对你而言必需的集中力和敏感度哦。');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1004, /*name*/'穗高未奏希', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'喂，哥哥！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我们今天玩什么？', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'未奏希我呀\n想玩捉迷藏！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1006, /*name*/'风宫茜里', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是风宫茜里', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'喂……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'要不要和我……\n一起去？');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1007, /*name*/'出云宫子', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫出云宫子——', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我想吃布丁——', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'食物的怨念是很可怕哦——');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1008, /*name*/'虹村雪', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是虹村雪。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'你也是被我的美貌\n吸引而来的吧。', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'可以哦。\n被大家欣赏就是我的命运啊……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1009, /*name*/'柊杏奈', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'哼……听完后就好好后悔吧！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我的真名为\n安妮罗洁・冯・修特帕鲁！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'人称「疾风之冥姬」！！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1010, /*name*/'姬宫真步', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是真步真步王国的公主，\n真步公主哟', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'小兔子，\n你指引着我与命中注定的王子相遇', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'真是感激不尽呢~♪');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1011, /*name*/'衣之咲璃乃', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是衣之咲璃乃！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'在这三千世界中\n寻找着与我分离的哥哥！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'在这里碰上我\n你的运气也到头了~！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1012, /*name*/'柏崎初音', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是初音，\n很强哦。\n闪亮亮☆', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'超、超能力……\n什么意思呀~？', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'……呃，拜托你！\n不要告诉任何人~！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1016, /*name*/'美波铃奈', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'你好啊！\n我是美波铃奈哦~♪', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'是一名兼任平面模特的女高中生哦！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'秀才简直\n感好（感觉真好）~♪');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1017, /*name*/'喜屋武香织', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'你好。\n我是喜屋武香织', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'东京就像游乐场一样', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'不过，冲绳也是个好地方哦');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1018, /*name*/'支仓伊绪', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫支仓伊绪。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'被称呼为老师\n实在太难为情了', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'请叫我小伊绪吧。');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1020, /*name*/'茜美美', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'唔哎……？\n太难的事情\n美美不懂……', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'啊！\n哥哥~\n等等我~', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'不要抛下美美~');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1021, /*name*/'栗林胡桃', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我……我叫……\n栗……林……胡桃……', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'啊……\n唔哎……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'唔哎哎哎哎哎……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1022, /*name*/'风宫依里', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫风宫依里。\n啊啊啊啊啊！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'谢谢你为了我，', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'安排出这么宝贵的时间！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1023, /*name*/'北条绫音', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫绫音！\n我和噗吉会一同追随你！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'哥哥想去哪里？\n诶？', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我想去这边\n快点快点——！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1025, /*name*/'天野铃莓', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我……我……\n我叫天野铃莓！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'小、小女子不才\n请与小女子喜结……咦，', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'好像不对……\n啊啊啊对不起！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1027, /*name*/'仓石惠理子', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'……呵呵……我是\n……仓石惠理子……', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'是你命中注定的……\n伴侣……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我是绝对……\n不会离开你的……！！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1028, /*name*/'佐佐木咲恋', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是佐佐木咲恋。\n你不觉得\n世界上有太多徒劳无功的事了吗？', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'如果可以省下徒劳的工夫，\n就能过上更加悠闲的生活！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'……你说对吧？？');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1029, /*name*/'樱井望', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫樱井望！\n请多多指教', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'唔……\n你居然不认识我？！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'啊哈哈，\n看来我还差得远啊~');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1030, /*name*/'妮侬·朱柏尔', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'咚咚咚！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'幸会，\n我叫妮侬·朱柏尔！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'诸君，\n和我一统天下吧！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1031, /*name*/'上喜忍', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫上喜忍。\n手上的骷髅是我的父亲。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'然后我身边的是……\n诶，你看不见吗？', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'奇怪了……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1033, /*name*/'野户真阳', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我的目标是\n成为超级搞笑艺人哟！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'但是\n却缺少一个条件……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'没错，\n就是跟我说相声的另一个人……！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1034, /*name*/'绫濑由加莉', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'你好……我是……绫濑由加莉……\n啊……对不起！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我有些紧张……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我\n我先去喝一杯……！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1036, /*name*/'冰川镜华', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是……冰川镜华', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'因为不能和陌生人说话', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'所以……\n我们的对话到此为止！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1038, /*name*/'柏崎栞', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是……柏崎栞。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'……啊，闹钟响了。', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我身体不好，\n每30分钟\n就必须休息。');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1040, /*name*/'双叶碧', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我、我叫双叶碧。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我一个朋友也没有……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'野花，\n是我唯一的倾诉对象。');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1042, /*name*/'三角千歌', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫三角千歌。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'我一直希望……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'自己的歌声，\n可以带给所有人幸福……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1043, /*name*/'安芸真琴', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫安芸真琴。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'哈？\n你也是这家伙的同伴吗？', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'对、对不起，我不该怀疑你的……');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1044, /*name*/'伊莉亚·奥恩斯坦', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'妾身乃黑夜的统治者！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'世间生灵皆听命！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'尽管臣服于\n妾身伟大的力量之下吧！\n哈哈哈！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1045, /*name*/'远见空花', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'然后骑士对妖精做出粗暴的……', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'唔呼呼呼呼……哈！', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'你、你找空花做什么！？');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1046, /*name*/'宫坂珠希', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'宫坂珠希喵♪\n猫咪真好啊喵？', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'无拘无束♪\n睡醒就玩耍，吃完就打架……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'唔~\n猫咪是最棒的喵~♪');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1048, /*name*/'大神美冬', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是大神美冬！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'马上开始攻略关卡吧……', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'……啊\n得去打工了！\n待会儿见！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1049, /*name*/'星野静流', /*love_level*/2, /*scale*/1.4, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我是星野静流哦！', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'不管是攻略关卡还是战斗\n打扫还是洗衣服', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'通通~\n包在姐姐身上！');
INSERT INTO `character_love_rankup_text` VALUES (/*chara_id*/1050, /*name*/'玉泉美咲', /*love_level*/2, /*scale*/1.5, /*position_x*/0, /*position_y*/0, /*voice_id_1*/0, /*face_1*/1, /*serif_1*/'我叫玉泉美咲。', /*voice_id_2*/0, /*face_2*/1, /*serif_2*/'如你所见\n是位性感的美少女', /*voice_id_3*/0, /*face_3*/1, /*serif_3*/'我可是学校里\n备受瞩目的偶像哦♪');
