INSERT INTO `equipment_data` VALUES (/*equipment_id*/102612, /*equipment_name*/'智慧宝珠', /*description*/'能给予战斗时必须的『仁·智·勇』的智之力宝珠。\n能使佩戴者头脑清醒，是提升魔法效果的智慧护身符。', /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102613, /*equipment_name*/'魔法单眼镜', /*description*/'高贵的魔法师所使用的单片眼镜。\n金属部分雕刻着卢恩文字，\n能提升穿戴者的魔力。', /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/17, /*hp*/0.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102614, /*equipment_name*/'花纹手镯', /*description*/'镶嵌着魔法石的银制手镯，\n上面刻有特殊的印记。\n可让魔法的流动更为活跃，\n提升穿戴者的魔力。', /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/16, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103011, /*equipment_name*/'皓耀剑', /*description*/'利用陨石碎片打造的单刃弯刀。\n刀身沐浴在光线之下\n便会像星尘般闪闪发亮。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/90.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103012, /*equipment_name*/'龙纹剑', /*description*/'剑身描绘着巨龙吐息的纹样。\n刀刃宽，比起突击更适合斩击，\n挥舞时彷如游龙在天。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/115.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103013, /*equipment_name*/'轻羽剑', /*description*/'某魔法师在近身战上使用的剑。\n过去气力小的他，\n在巨鸟的羽毛上施加魔法，\n做出了极为轻盈的剑。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/37, /*hp*/135.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103041, /*equipment_name*/'月光刀', /*description*/'东国某剑派流传给继承者的刀剑。\n寄宿着月光的力量，\n能使持有者的五感和灵感。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/3000, /*require_level*/30, /*hp*/65.0, /*atk*/58.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103042, /*equipment_name*/'雷神刀', /*description*/'某位本领高超的武士在击退雷兽时使用的刀。\n斩杀雷兽的刀身上，\n寄宿着雷电之力。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/40, /*hp*/75.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103071, /*equipment_name*/'白金匕首', /*description*/'使用高价白金打造而成的短剑。\n很多贵族藏在怀里以做防身之用。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103072, /*equipment_name*/'吸血鬼短剑', /*description*/'拥有意识，\n渴望着鲜血的可怕短剑。\n沾上血后不但不生锈，\n反而会发出更为妖媚的光芒。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103101, /*equipment_name*/'龙爪', /*description*/'以残暴黑龙的下颚为原型的钩爪。\n属于暗器的一种，\n过去使用该武器的刺客，会在钩爪上涂上毒，以提高杀伤力。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103131, /*equipment_name*/'丘比特之弓', /*description*/'曾有一名男子将这把弓射获的猎物\n赠与喜欢的人并表白成功。\n因为这件事，\n他为这把弓取了跟爱神一样的名字。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103132, /*equipment_name*/'恶魔弓', /*description*/'受到人类迫害，被放逐到地下深处的魔族所制作的弓。\n他们利用这把弓，\n把侵入其领域的人类通通葬送。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103161, /*equipment_name*/'骑士的荣耀', /*description*/'赠与名誉骑士的矛。\n其刀刃不会被折断，\n体现了为国为民战斗的骑士的荣耀。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/18, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103162, /*equipment_name*/'绝对之矛', /*description*/'一击必杀的矛。\n其强大的杀伤力，\n能轻易贯穿坚固的铠甲，\n一击便能杀死敌人。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/46.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103191, /*equipment_name*/'蓝星斧', /*description*/'装饰着蓝色天体的战斧。\n象征着世界本身的天体，\n能吸收大地的能量，\n给予持有者力量。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103192, /*equipment_name*/'丽花战斧', /*description*/'装饰着华丽花朵的斧头。\n每次挥舞，\n都会绽放大朵的鲜血之花。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103193, /*equipment_name*/'雷电斧', /*description*/'某个研究先进武器的工房\n制作出来的斧头。\n斧头内储存着雷电，\n攻击时可进行释放。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/66, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103221, /*equipment_name*/'刺血法杖', /*description*/'某些残忍的魔法师团体\n所使用的法杖。\n法杖吸收了牺牲者们的鲜血，\n变得通体赤红。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103222, /*equipment_name*/'恶魔之角', /*description*/'传说中某位魔法师与恶魔进行交易，\n从而得到的武器。\n该法杖吸收战败敌人的灵魂，\n然后上供给恶魔。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103251, /*equipment_name*/'天球护身法杖', /*description*/'某个以占星术为生的魔法师一族\n所使用的法杖。\n他们通过观测星体，\n占卜未来。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103252, /*equipment_name*/'花蕾法杖', /*description*/'救死扶伤的意念\n能让花蕾绽放，\n那纯净的能量，\n能成为治愈的力量。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/0.0, /*atk*/0.0, /*magic_str*/53.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103281, /*equipment_name*/'时髦盔甲', /*description*/'以强大时尚的骑士为主题\n制作而成的铠甲。\n但不管有多时尚，\n实力不足的话，魅力也会大减。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103282, /*equipment_name*/'战裙', /*description*/'将强壮的男人逐一打倒，\n成为斗技场女王的女战士铠甲。\n对方只要被她野兽般的目光凝视，\n便会丧失战意。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/42, /*sale_price*/3000, /*require_level*/32, /*hp*/325.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103283, /*equipment_name*/'重金属护甲', /*description*/'用厚重铁板制作成的铠甲。\n抛弃轻便性，\n强化了防御力，\n使用斧头和大剑的战士最爱穿着。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/380.0, /*atk*/17.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103311, /*equipment_name*/'华丽护甲', /*description*/'喜爱华丽的骑士委托设计师\n打造的铠甲。\n在战场上，因为打扮华丽而特别显眼\n的他，正好成为了敌人的目标。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103341, /*equipment_name*/'千层服', /*description*/'在织物上叠加皮革长袍做成的衣服。\n防寒效果一流，\n适合穿着前往寒冷之地采集素材。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/180.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/160.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103342, /*equipment_name*/'宫廷服', /*description*/'王城内工作的女骑士服装。\n使用了优质的皮革和黑金，\n胸口的装饰彰显骑士的地位。\n每一处都设计体现着高雅的品味。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/210.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103343, /*equipment_name*/'秩序法衣', /*description*/'精灵女王参加和其他种族\n举办的和平协议会议时穿着的服装。\n精灵族为了以防万一，\n在衣服上张开了强大的魔法结界。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/75, /*sale_price*/4000, /*require_level*/44, /*hp*/205.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/370.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103371, /*equipment_name*/'丘比特长袍', /*description*/'以传说中登场的爱神的服装\n为原型制作的长袍。\n背面的羽毛依靠魔法飘起，\n看起来就像真的一样。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103372, /*equipment_name*/'哲学家长袍', /*description*/'身穿这件长袍的哲学家，\n为辅佐幼帝倾尽一生。\n他引导皇帝走向正途，\n将国家带向了更美好的未来。', /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/245.0, /*atk*/0.0, /*magic_str*/14.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
