INSERT INTO `equipment_data` VALUES (/*equipment_id*/105431, /*equipment_name*/'地狱胫甲', /*description*/'征战沙场的战姬在倒在敌人刀下前\n一直穿着的军靴。\n由于仅差一步就能得到天下，\n其怨念形成了庞大魔力，并寄宿于靴子之中。', /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/77, /*hp*/60.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/6.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105521, /*equipment_name*/'忘哭之冠', /*description*/'已被灭国并被遗忘的皇女曾佩戴过的王冠。下一位持有者能感应到其中蕴含的哀伤，并得到膨大的魔力。', /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/0.0, /*magic_str*/48.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105522, /*equipment_name*/'白祈圣冠', /*description*/'赠予深受民众爱戴的\n圣女的王冠。心境清明的人\n穿戴之后，就能发挥\n辟邪的作用。', /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/22.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
