INSERT INTO `equipment_data` VALUES (/*equipment_id*/104043, /*equipment_name*/'鹰神之煌剑', /*description*/'支配天空的崇高鹰神之剑。\n只要挥动该剑，就能像瞄准猎物的老鹰一般，用魔力加速的剑刃\n精确捕捉敌人的要害。', /*original_equipment_id*/104043, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104044, /*equipment_name*/'凤凰刀', /*description*/'该刀寄宿着于日出之处现身的凤凰神的力量。刀身的火焰随使用者意志之强大而发生变化，意念强韧之人能使其产生熔化钢铁的烈火。', /*original_equipment_id*/104044, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104071, /*equipment_name*/'珍贵匕首', /*description*/'传说是某位矮人工匠向人类之王\n献上的匕首。\n该匕首所使用的材料\n与现存的矿石皆不一致。', /*original_equipment_id*/104071, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104072, /*equipment_name*/'公主短剑', /*description*/'历史上最悠久的小国的王族中流传的\n短刀。 该刀由王女代代传承，\n能驱除附在王女身上的魔怪，\n持续守护国家的安宁。', /*original_equipment_id*/104072, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/50.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/300.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
