INSERT INTO `equipment_data` VALUES (/*equipment_id*/104074, /*equipment_name*/'独角兽匕首', /*description*/'只有纯洁无瑕的女性\n才能使用的短剑。\n能阻挡一切污秽之物，\n邪恶之人甚至无法触碰。', /*original_equipment_id*/104074, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/160, /*sale_price*/18000, /*require_level*/59, /*hp*/100.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104101, /*equipment_name*/'火焰护臂', /*description*/'以炼狱之火锻造而成的深红手套。\n从拳头发出的业火，\n能将眼前的敌人烧得一干二净。', /*original_equipment_id*/104101, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/50, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104102, /*equipment_name*/'狮子王的护臂', /*description*/'被称为狮子王的名君曾使用的\n黄金手套。\n该手套能给予使用者无限的力量，\n一夫当关，万夫莫开。', /*original_equipment_id*/104102, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/111.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/9.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104103, /*equipment_name*/'暴雪之爪', /*description*/'封印了以一敌万的\n冰之女神的魔力的勾爪。\n狂暴之风如暴风雪一般冻结，\n削弱敌人的战意。', /*original_equipment_id*/104103, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104104, /*equipment_name*/'苍雷拳', /*description*/'受勇猛雷神庇佑的拳套。\n释放出的苍蓝雷击\n能瞬间将敌人变成焦炭，\n甚至能召唤雷云。', /*original_equipment_id*/104104, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104131, /*equipment_name*/'雷光弓', /*description*/'英雄为了打倒食人海龙\n将雷电之力封印于此弓。\n箭矢上缠绕着雷光，\n能一击葬送海龙。', /*original_equipment_id*/104131, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/145, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104132, /*equipment_name*/'天使弓', /*description*/'旧时代终焉之战中\n大天使对阵恶魔时使用的大弓。\n这把弓射出的一击，\n能抵达地狱最深处的悲叹之河。', /*original_equipment_id*/104132, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/4.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104133, /*equipment_name*/'阿尔忒弥斯之弓', /*description*/'蕴含着狩猎女神加护的弓。\n弓箭具有绝不放过猎物的百发百中\n以及破魔的效果，\n射出一箭就可改变战局。', /*original_equipment_id*/104133, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104134, /*equipment_name*/'精灵树之弓', /*description*/'以精灵树为素材制成的弓。\n蕴含着精灵界的\n神圣魔力，射出的箭矢\n能贯穿所有敌人。', /*original_equipment_id*/104134, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/60.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104161, /*equipment_name*/'绯龙矛', /*description*/'这把矛曾经插在栖息在火山之龙的背上。\n因矛头常年深入龙的体内，\n所以寄宿了火焰之力。', /*original_equipment_id*/104161, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104162, /*equipment_name*/'女王之矛', /*description*/'传说中统领大陆全境的女王曾使用的巨矛。\n才智过人、骁勇善战的她，\n凭借这把矛将大陆尽收手中。', /*original_equipment_id*/104162, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104163, /*equipment_name*/'兴风者', /*description*/'传说中的枪骑士持有的名枪。\n一挥动，真空之刃就会化为旋风，\n不直接触碰到刀刃，\n周围的敌人也会被一网打尽。', /*original_equipment_id*/104163, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/205, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/5.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104164, /*equipment_name*/'盖亚之矛', /*description*/'受大地之母庇佑的矛。\n绝不会折断或碎裂。\n其强韧将融入持有者体内，\n大幅度提升持有者的腕力。', /*original_equipment_id*/104164, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104191, /*equipment_name*/'紫龙斧', /*description*/'各地英豪齐聚一堂，\n将威胁人世的邪龙封印于此斧。\n寄宿着龙魂的这把斧头，\n散发着不详的能量。', /*original_equipment_id*/104191, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104192, /*equipment_name*/'地狱火之斧', /*description*/'封印着地狱业火的双刃斧。\n刀身如同鲜血般赤红，\n挥动起来，红莲之焰便向敌人袭去，\n连骨头都燃烧殆尽。', /*original_equipment_id*/104192, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/97.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104193, /*equipment_name*/'战女王的烈斧', /*description*/'一直站在战场最前线的女王的战斧。\n斧头的重量超越想象，\n而斧头承认的所有者却能轻松使用。\n一击就可以打破坚固的城门。', /*original_equipment_id*/104193, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/114.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
