INSERT INTO `equipment_data` VALUES (/*equipment_id*/104284, /*equipment_name*/'猩红铠甲', /*description*/'曾对抗黑暗女皇、守护国家的骑士姬\n身穿的猩红铠甲。\n其神圣的光辉能吓退敌人，\n为民众带来勇气。', /*original_equipment_id*/104284, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/160.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
