INSERT INTO `equipment_data` VALUES (/*equipment_id*/104313, /*equipment_name*/'紫罗兰盔甲', /*description*/'作为战场的死神而被人畏惧的\n大将军身穿的盔甲。\n据说敌军只要看到这个紫色的盔甲\n就会吓得魂飞魄散。', /*original_equipment_id*/104313, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/31, /*craft_flg*/1, /*equipment_enhance_point*/252, /*sale_price*/15000, /*require_level*/54, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
