INSERT INTO `equipment_data` VALUES (/*equipment_id*/104611, /*equipment_name*/'新月的悲叹', /*description*/'因被算计而用弓杀害了\n心爱之人的月之女神的护身符。\n充满哀伤的下弦之月，魔力可击退箭矢。', /*original_equipment_id*/104611, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/50.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/6.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104612, /*equipment_name*/'龙之泪', /*description*/'龙流的泪经过漫长的时间而结晶化，\n成为极为稀少的矿石。\n传说其中栖宿着龙之力，\n可以直接震撼装备者的灵魂。', /*original_equipment_id*/104612, /*promotion_level*/4, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/120.0, /*atk*/0.0, /*magic_str*/70.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
