INSERT INTO `equipment_data` VALUES (/*equipment_id*/105011, /*equipment_name*/'闪电之刃', /*description*/'从雷神劈下的怒雷之中\n诞生的大剑。\n一挥即可招来劈裂天空的闪电，\n横扫千军。', /*original_equipment_id*/105011, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
