INSERT INTO `equipment_data` VALUES (/*equipment_id*/105013, /*equipment_name*/'罪过的大剑', /*description*/'不必斩人也能祓除罪恶的大剑。\n被此剑砍到的罪人不会受任何伤，\n但代价是需要将其余生奉献给赎罪一途。', /*original_equipment_id*/105013, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/475, /*sale_price*/30000, /*require_level*/80, /*hp*/140.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/8.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105014, /*equipment_name*/'水之支配者神凑剑', /*description*/'被供奉在海神居住的深海神殿中的\n水之神剑。以水化成的刀刃千变万化，可以变换成洪水、旋涡、雾气等多种形态来压制敌人。', /*original_equipment_id*/105014, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/590, /*sale_price*/32000, /*require_level*/88, /*hp*/160.0, /*atk*/132.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105015, /*equipment_name*/'霸王光龙剑', /*description*/'寄宿了传说中的存在、光龙之力的\n大剑。由于太过锋利，触碰到的事物\n无一可幸免，因此在其之上篆刻了黄金花纹，以此抑制力量。', /*original_equipment_id*/105015, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/565, /*sale_price*/32000, /*require_level*/94, /*hp*/60.0, /*atk*/145.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105016, /*equipment_name*/'勇气星核剑', /*description*/'统治大地的母亲神，赐予有正直之心的战王的矿石之剑。刺入大地便能召唤坚硬且锐利的铁柱并一直延伸至大地的尽头，击溃敌军。', /*original_equipment_id*/105016, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/465, /*sale_price*/32000, /*require_level*/100, /*hp*/80.0, /*atk*/153.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
