INSERT INTO `equipment_data` VALUES (/*equipment_id*/105041, /*equipment_name*/'百华白樱刀', /*description*/'白光流泻，如同盛开樱花一般\n美丽的刀。当刀柄上的铃铛发出\n声响，便能如落英般\n轻盈地击倒敌人。', /*original_equipment_id*/105041, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/118.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105042, /*equipment_name*/'流祸苍刃', /*description*/'因天地变异而沉没于水底之帝国的\n魔法剑。刀刃上刻下了国家的历史。\n受水精灵庇佑的刀身\n能使出如流水般变幻莫测的斩击。', /*original_equipment_id*/105042, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105043, /*equipment_name*/'妖刀血鸦', /*description*/'一把时刻渴望着血液、受到诅咒的刀。如果判断使用者不配挥舞它便会咒杀使用者，将其转化为缠绕在刀上的部分妖气。', /*original_equipment_id*/105043, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/592, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/125.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105044, /*equipment_name*/'烈火焰神刀', /*description*/'由火神之焰锻造而成的赤红刀剑。\n超高温的刀身能烧毁一切事物，释放火焰后甚至能将敌人庞大的军队瞬间化为灰烬。', /*original_equipment_id*/105044, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/88, /*hp*/80.0, /*atk*/134.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105045, /*equipment_name*/'翠风神的太刀', /*description*/'由风神所自由挥舞的太刀。\n哪怕敌人尚未碰触它，也会被缠绕在刀身上的无数疾风利落地切割。', /*original_equipment_id*/105045, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/741, /*sale_price*/32000, /*require_level*/94, /*hp*/100.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105046, /*equipment_name*/'降神的枝刃刀', /*description*/'曾用于降神仪式上的祭礼之剑。\n神明之光至今仍寄宿于刀刃之上，只用轻轻一挥就能祓除邪恶。', /*original_equipment_id*/105046, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/544, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/150.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/2.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105047, /*equipment_name*/'旺鲤之神刀', /*description*/'这是镇压了发狂水神的巫女所挥舞的\n灵刀。如同遨游天际的神鱼般美丽的\n刀刃，和如同跳着神乐舞一般的\n剑闪，据说连神都为之倾倒。', /*original_equipment_id*/105047, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/172.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
