INSERT INTO `equipment_data` VALUES (/*equipment_id*/105077, /*equipment_name*/'冥刃·鸦天狗', /*description*/'一把漆黑的魔剑，寄宿着掌管死亡的\n黑翼灵鸟的魔力。其黑刃甚至能\n弑神，当它施展出疾风般冷酷的剑闪\n时，能将所有生命送回冥府。', /*original_equipment_id*/105077, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/153.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/4.0, /*display_item*/0, /*item_type*/0);
