INSERT INTO `equipment_data` VALUES (/*equipment_id*/105103, /*equipment_name*/'悲叹河之爪', /*description*/'用冥府之冰精炼而成的冰爪。\n被此爪割裂的人会在瞬间被冻结，变成无言的冰像成为冥府的住民。', /*original_equipment_id*/105103, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105104, /*equipment_name*/'岚神风暴护手', /*description*/'寄宿了支配暴风雨之神的力量的护手。被暴风包裹的护手不断发出的打击犹如猛烈的龙卷风。战斗结束时，周围只会剩下一望无际的无人荒野。', /*original_equipment_id*/105104, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/605, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/124.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105105, /*equipment_name*/'极暗爪血色护手', /*description*/'由巨狼狂爪的碎片制成的护手。\n获得压倒性的力量，代价是不成熟之人使用该护手时，身心都会被侵蚀，受到诅咒的同时感到畏惧。', /*original_equipment_id*/105105, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/111.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105106, /*equipment_name*/'勇气星核拳', /*description*/'统治大地的母亲神，赐予反抗暴政的战士的岩石之拳。宿有地底沸腾岩浆的能量一击即可破坏万物。', /*original_equipment_id*/105106, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/605, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/154.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105107, /*equipment_name*/'通天手甲·犀打', /*description*/'传说是由原野的灵兽幻化而成的手部\n兵器。据说，只要被骁勇善战的人\n使用，其身心就能超过钢铁的顽强，\n从而到达武之境界的入口。', /*original_equipment_id*/105107, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/133.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/28.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105131, /*equipment_name*/'白银大翼弓', /*description*/'以拥有白银之翼的鸟为原型的弓。\n射出的箭矢如同翔空之鸟，\n干脆利落的一击\n能贯穿魔怪。', /*original_equipment_id*/105131, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/245, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105132, /*equipment_name*/'深渊之弓', /*description*/'受魔族女王庇佑的妖弓。\n射出的箭矢带着不祥的魔力，\n如同融入黑暗一般\n毫无征兆地袭击敌人。', /*original_equipment_id*/105132, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/7.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105133, /*equipment_name*/'红天弓', /*description*/'传说中的弓手，于弓断箭尽之时获得的天赐圣弓。可以承受任何射击速度，将箭送至弓手瞄准的任意地方。', /*original_equipment_id*/105133, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/126.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105134, /*equipment_name*/'宙斯坠落装号弓', /*description*/'神话中的大英雄使用的巨大弓箭。\n常人无法拿起它，据说射出的箭矢甚至能够划破天空击坠支配天空的龙。', /*original_equipment_id*/105134, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/132.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105135, /*equipment_name*/'冰冻泪弓', /*description*/'用冬之女神流下的眼泪结晶制作而成的弓。射出的箭矢上覆有女神的加护，能够永久冰冻任何难敌。', /*original_equipment_id*/105135, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/137.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105136, /*equipment_name*/'勇气星核弓', /*description*/'统治大地的母亲神，赐予挑战击退邪龙的英雄的顽强大弓。充盈着大地能量的弓所射出的箭矢能够一举击穿天上闪耀的星星。', /*original_equipment_id*/105136, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/139.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105137, /*equipment_name*/'天穿之隼弓', /*description*/'有飞天精灵附身的狩弓。缠绕着狂风\n的一箭能贯穿一切，\n据说，如果是精灵认可的射手，\n还能掀起暴风雨。', /*original_equipment_id*/105137, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/630, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/160.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/22.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105161, /*equipment_name*/'冰霜矛', /*description*/'该冰矛被封印在传说中的冰狱之中。\n被冰矛刺中的目标，\n会连同哀嚎一起被冰封起来。', /*original_equipment_id*/105161, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/98.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105162, /*equipment_name*/'风神矛', /*description*/'受风神恩惠、\n出身正统的大矛。\n使出的一击化为一闪而过的疾风，\n将一切吹飞。', /*original_equipment_id*/105162, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/305, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/92.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105163, /*equipment_name*/'护天的圣枪', /*description*/'守护圣域之人挥舞的神圣之枪。\n枪尖并不指向敌人，可脚下邪恶之人的骸骨还在源源不断地增加。', /*original_equipment_id*/105163, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105164, /*equipment_name*/'毁灭之伤冥神枪', /*description*/'支配幽冥的神所挥舞的魔枪。\n赤红的枪尖迸发出被幽禁在地狱的咎神那充满憎恶与黑暗的魔力，不断重复着永无止境的杀戮。', /*original_equipment_id*/105164, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/692, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/113.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105165, /*equipment_name*/'日珥豪炎枪', /*description*/'大名鼎鼎的锻冶师以炎帝之名打造的传说之枪。枪尖缠绕着灼热的业火，犹如神明之怒，将烧穿神罗万象。', /*original_equipment_id*/105165, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/141.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105166, /*equipment_name*/'勇气星核枪', /*description*/'统治大地的母亲神，赐予反抗掌管灾厄的邪恶女神的勇者的重枪。充盈着大地力量的一击转瞬就能将世间万物化为尘埃。', /*original_equipment_id*/105166, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/100, /*hp*/150.0, /*atk*/127.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105167, /*equipment_name*/'圣鹿枪阿尔玛贝娜德', /*description*/'以守护神域的圣兽为原型的灵枪。\n掌管着大自然中的生命，据说一次\n攻击就能让荒芜的死亡之地\n萌发出新的生命。', /*original_equipment_id*/105167, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/418, /*sale_price*/32000, /*require_level*/106, /*hp*/250.0, /*atk*/139.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105191, /*equipment_name*/'死灵斧', /*description*/'封印着冥界之力的斧头。\n掌管死亡的使者在执行任务时使用。\n伴随着斧头滞钝的声音，\n将生者送往冥界。', /*original_equipment_id*/105191, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/400, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/1.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105192, /*equipment_name*/'神判圣斧', /*description*/'天界执行裁决时\n使用的神圣斧头。\n罪人罪孽越是深重\n威力便越大，彰显着神之威光。', /*original_equipment_id*/105192, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105193, /*equipment_name*/'盖亚之斧', /*description*/'古代女战士挥舞的战斧。\n据传被该战斧砍杀之人，会被迎接至众神所居住的富饶的理想乡。', /*original_equipment_id*/105193, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105194, /*equipment_name*/'撼地神轰斧', /*description*/'巨神族喜欢使用的粗糙战斧。\n由神界巨石削制而成的刀身有着备受赞叹的可怕硬度和重量，仅用一击就能改变四周的地形。', /*original_equipment_id*/105194, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/585, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/140.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105195, /*equipment_name*/'碎核机操斧', /*description*/'由蒸汽这一古代繁荣文明中的遗失技术所制成的带有变形机关的战斧。据说将输出功率调至最大后再挥舞甚至可以粉碎星星的核心。', /*original_equipment_id*/105195, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/642, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/151.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105196, /*equipment_name*/'绿松石·诺亚', /*description*/'用存在于神代的沧海深水冶炼而成的战斧。由庞大的水量凝缩而成的超重斧刃，能够产生惊涛骇浪般具有破坏性的洪流，吞噬眼前的所有敌人。', /*original_equipment_id*/105196, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/580, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/170.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105197, /*equipment_name*/'炎熊帝之爪战斧', /*description*/'这把斧头上寄宿着被称为山神的火熊\n的魂魄。据说，与灵魂共鸣而放出的灼热一斩，\n能给我方带来丰收与繁荣，\n给敌方带来毁灭与衰退。', /*original_equipment_id*/105197, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/585, /*sale_price*/32000, /*require_level*/106, /*hp*/200.0, /*atk*/150.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105221, /*equipment_name*/'异界法杖', /*description*/'封印着异界魔力的法杖。\n虽然拥有爆发性的威力，\n但实力不足的人，\n是无法释放其力量的。', /*original_equipment_id*/105221, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/3.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
