INSERT INTO `equipment_data` VALUES (/*equipment_id*/105223, /*equipment_name*/'雷霆之杖', /*description*/'将响彻天际的闪电之力封入其中的神圣魔杖。这把魔杖过于猛烈的力量是常人难以负担的，但只要使用一次就能烧毁任何敌人。', /*original_equipment_id*/105223, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/525, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/118.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105224, /*equipment_name*/'黑蛇龙之杖', /*description*/'封入在诸神之战中幸存下来的龙之魂的魔杖。灯笼散发的黑色魔光可葬送敌人，吞噬灵魂。吞噬越多的灵魂灯光就会变得越发强烈而妖艳。', /*original_equipment_id*/105224, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/770, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/0.0, /*magic_str*/128.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/16.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
