INSERT INTO `equipment_data` VALUES (/*equipment_id*/105226, /*equipment_name*/'清雅翠蝶杖', /*description*/'使用了世间最美丽的灵蝶翅膀制成的魔杖。翠蝶翅膀产生的风和闪耀的鳞粉可麻痹敌人的感官，不费吹灰之力便能压制狂暴的魔物。', /*original_equipment_id*/105226, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/0.0, /*magic_str*/154.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105227, /*equipment_name*/'黑蛇魔炎杖', /*description*/'魔杖上面铭刻着记载于神话中的\n蛇群之霸王的灵魂。其释放出的业火\n会紧紧缠绕着敌人不放，\n甚至能将灵魂也燃烧殆尽。', /*original_equipment_id*/105227, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/477, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/0.0, /*magic_str*/172.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
