INSERT INTO `equipment_data` VALUES (/*equipment_id*/105282, /*equipment_name*/'九天之铠', /*description*/'由失传的太古技术所制造而成的铠甲。\n穿戴者将被赐予看不见的翅膀，\n纵横沙场。', /*original_equipment_id*/105282, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/392, /*sale_price*/27000, /*require_level*/77, /*hp*/80.0, /*atk*/42.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105283, /*equipment_name*/'愤怒女王的礼裙', /*description*/'支配着幽暗深山的盗贼女王所穿的铠甲。穿上后就会变得如魔兽般强韧而狞猛，在血肉横飞的战场上闪耀的野性之美，令所有人心怀畏惧。', /*original_equipment_id*/105283, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/362, /*sale_price*/30000, /*require_level*/85, /*hp*/80.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105284, /*equipment_name*/'闪华舞铠', /*description*/'传说中的舞兵一族在战场上身着的轻铠。其重量如飞舞的花瓣般轻盈，但又强韧到足以抵御一切猛攻。', /*original_equipment_id*/105284, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/93, /*hp*/0.0, /*atk*/48.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105285, /*equipment_name*/'灵魂玫瑰丽装', /*description*/'传说中亲自上阵杀敌的军国公主身穿的玫瑰铠甲。穿上后将被高贵的灵魂寄宿，不会再屈服于任何绝望和恐怖。', /*original_equipment_id*/105285, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/795, /*sale_price*/32000, /*require_level*/99, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/2.0, /*physical_critical*/9.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
