INSERT INTO `equipment_data` VALUES (/*equipment_id*/105376, /*equipment_name*/'极西天之圣导衣', /*description*/'被掌管西天的守护天所庇佑的月白之\n法衣。据说它充满了引导混乱世界的\n光明，能获得守护天使清高的祝福与\n启示。', /*original_equipment_id*/105376, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/507, /*sale_price*/32000, /*require_level*/105, /*hp*/0.0, /*atk*/0.0, /*magic_str*/52.0, /*def*/4.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/15.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105377, /*equipment_name*/'绯天之郁金香礼服', /*description*/'以绽放于绯天的牡丹百合花瓣编织而\n成的深红礼服。掌管热情的花之加护\n授予了着装者不屈不挠的精神，使之\n从一切危机中全身而退。', /*original_equipment_id*/105377, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/32000, /*require_level*/111, /*hp*/0.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/6.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
