INSERT INTO `equipment_data` VALUES (/*equipment_id*/105402, /*equipment_name*/'炽白银的镜盾', /*description*/'以神界之银和数千魔术组合而成的镜之盾。那没有任何划伤的光洁镜面，甚至可以反射禁咒。', /*original_equipment_id*/105402, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/40, /*craft_flg*/1, /*equipment_enhance_point*/726, /*sale_price*/32000, /*require_level*/99, /*hp*/80.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/14.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105403, /*equipment_name*/'圣圆环神洁莲', /*description*/'这是给混沌的世界带来清净的圣莲花\n的圆盾。据说，蕴含了破邪祈愿的\n纹样有时还会赌上大朵的花瓣\n来保护自己的主人摆脱困境。', /*original_equipment_id*/105403, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/40, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/111, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/17.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
