INSERT INTO `equipment_data` VALUES (/*equipment_id*/105433, /*equipment_name*/'极翼天之圣靴', /*description*/'被掌管天空的守护天使们赐予加护\n的战靴。装饰着四方天使所授予的\n祝福羽毛，寄宿了驱除万象诅咒\n与苦难的无与伦比的神圣性。', /*original_equipment_id*/105433, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/43, /*craft_flg*/1, /*equipment_enhance_point*/620, /*sale_price*/32000, /*require_level*/105, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/18.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
