INSERT INTO `equipment_data` VALUES (/*equipment_id*/105617, /*equipment_name*/'苍晚夜想曲的胸针', /*description*/'充满了苍茫夜色的光辉。优美的\n夜曲旋律通过驱除邪念与\n净化精神来引出穿戴者\n更多的潜在能力。', /*original_equipment_id*/105617, /*promotion_level*/5, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/108, /*hp*/0.0, /*atk*/0.0, /*magic_str*/105.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/25.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106011, /*equipment_name*/'辉光剑征服者', /*description*/'用最初的金刚石切割锻造而成的传说\n中的宝石剑。无论历经了多少岁月都\n能持续释放出永恒的光芒。永世不\n折，以其耀眼的光辉斩妖除魔。', /*original_equipment_id*/106011, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/155.0, /*atk*/191.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/21.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106012, /*equipment_name*/'巧克力棒长剑', /*description*/'在点心之国流传已久的知名巧克力宝\n剑。与其甜点的外观相反，优点是它\n的锐利锋芒。对冒险者们来说是一件\n垂涎欲滴的宝物。', /*original_equipment_id*/106012, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/820, /*sale_price*/34000, /*require_level*/118, /*hp*/75.0, /*atk*/216.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106013, /*equipment_name*/'炎天赤日剑', /*description*/'附有能发出阳炎的太阳光。\n隐藏在其中的庞大魔力\n能将装备者的力量提升至极限，\n将所有敌人燃烧殆尽。', /*original_equipment_id*/106013, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/870, /*sale_price*/36000, /*require_level*/124, /*hp*/55.0, /*atk*/235.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/18.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106041, /*equipment_name*/'红玉刀绯红之月', /*description*/'用红玉的结晶打磨而成的带有魔性的\n妖刀。红色的月牙状刀身渴求着鲜\n血，其超群的锋芒能斩杀一切拥有生\n命的生物。', /*original_equipment_id*/106041, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/206.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/26.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106042, /*equipment_name*/'冰淇淋刀', /*description*/'这是从点心之国传来的刀，\n拥有冰淇淋的刀身。十分光滑，\n可以自然弯曲，让变幻自如的刀法\n成为可能。', /*original_equipment_id*/106042, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/118, /*hp*/0.0, /*atk*/216.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/37.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106043, /*equipment_name*/'薰风花散', /*description*/'这把刀里寄宿着平稳穿过壮阔森林\n的一阵风。如风中飞舞的花瓣一般\n清爽流畅的太刀法令人无法抵挡，\n一击致命。', /*original_equipment_id*/106043, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/1200, /*sale_price*/36000, /*require_level*/124, /*hp*/0.0, /*atk*/215.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/39.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106071, /*equipment_name*/'翠风刃祖母绿短剑', /*description*/'这把翠玉宝石剑中寄宿着掌管风的妖\n精王的力量。拥有能使风之妖精\n听命的权利，他们刮起的飓风\n能吹飞一切邪恶。', /*original_equipment_id*/106071, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/720, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/198.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/42.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106072, /*equipment_name*/'巧克力香蕉短剑', /*description*/'用巧克力香蕉制作而成的点心之国的\n秘传短剑。凝固的巧克力表面的牢固\n度和香蕉粘着的刀身使其锋利无比。', /*original_equipment_id*/106072, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/118, /*hp*/250.0, /*atk*/199.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106073, /*equipment_name*/'紫雾短剑', /*description*/'这把短剑用禁忌的咒法封锁了迷惑\n一切的浓雾。\n挥刀后，从刀身冒出的烟雾会将\n装备者隐藏起来，进而迷惑敌人。', /*original_equipment_id*/106073, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/1160, /*sale_price*/36000, /*require_level*/124, /*hp*/0.0, /*atk*/212.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106101, /*equipment_name*/'蛋白石抹灭之拳', /*description*/'以附有神之力的宝石——掌管希望的\n蛋白石为装饰的拳头兵器。据说，\n释放出希望之光的拳头能埋葬一切魔\n物，将人们从绝望中拯救出来。', /*original_equipment_id*/106101, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/174.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106102, /*equipment_name*/'泡芙拳套', /*description*/'是点心之国传说中的拳王爱用的\n泡芙拳甲。能柔软地包裹住\n对装备者的冲击，并将反作用力\n全都反击给对方。', /*original_equipment_id*/106102, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/118, /*hp*/0.0, /*atk*/225.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106103, /*equipment_name*/'沙尘瀑布手甲', /*description*/'犹如壁垒般遮天蔽日的沙尘——将其\n凝结而成的手甲。给予装备者巨石般\n的坚固与腾空而起的轻盈，即使和\n千军万马对阵也可全身而退。', /*original_equipment_id*/106103, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/36000, /*require_level*/124, /*hp*/0.0, /*atk*/220.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106131, /*equipment_name*/'苍神弓仁慈蓝玉', /*description*/'用神赐予人类的灵石——蓝宝石制\n作而成的神弓。掌管慈爱，射出的箭\n矢散发着苍蓝色的光辉，甚至可以击\n退将人类堕入邪恶的魔神。', /*original_equipment_id*/106131, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/144.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106132, /*equipment_name*/'甜甜糖果弓', /*description*/'在点心之国制作的彩色透糖工艺的弓。每次放箭的时候，\n长期积累在材料中的\n甜美香气便会笼罩在四周。', /*original_equipment_id*/106132, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/118, /*hp*/0.0, /*atk*/195.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106133, /*equipment_name*/'白雨流转弓', /*description*/'将狂风之中像针刺一般倾泻而下的飞\n雨捆绑而成打造的弓。每射出一箭，\n无穷无尽的箭雨就会保护装备者，同\n时也能抑制敌人的动作，一击致命。', /*original_equipment_id*/106133, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/1080, /*sale_price*/36000, /*require_level*/124, /*hp*/0.0, /*atk*/196.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/10.0, /*physical_critical*/8.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106161, /*equipment_name*/'水晶剥夺圣枪', /*description*/'神之力结晶后的水晶之枪。蕴含着神\n圣的力量，释放出圣光的一击甚至能\n消灭不死之王，因此，也被称为掌管\n净化的枪。', /*original_equipment_id*/106161, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/520, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/199.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106162, /*equipment_name*/'巧克力长枪', /*description*/'据说在点心之国发现了一把巧克力遮阳\n伞，这把长枪就是它的巨型版本。精\n心计算的重心平衡让它拥有很高的性\n能。', /*original_equipment_id*/106162, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/118, /*hp*/0.0, /*atk*/200.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106163, /*equipment_name*/'电闪雷光之枪', /*description*/'拥有贯穿天地之雷光的钢枪。\n由于蕴藏着庞大的力量，它可以自行\n选择装备的人，但释放出的一击却拥\n有无与伦比的破坏力。', /*original_equipment_id*/106163, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/840, /*sale_price*/36000, /*require_level*/124, /*hp*/0.0, /*atk*/215.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106191, /*equipment_name*/'黄铜裂战斧', /*description*/'与铭刻在英雄传说中的勇敢战神一生\n相伴的大战斧。仿若黄金般的雷光就\n是恐惧的象征，名震战场。', /*original_equipment_id*/106191, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/720, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/219.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106192, /*equipment_name*/'可丽饼战斧', /*description*/'点心之国的第一战士所使用的可丽饼战斧。口感丰富多彩，\n刀刃处形状复杂，\n以强大的破坏力著称。', /*original_equipment_id*/106192, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/118, /*hp*/0.0, /*atk*/235.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
