INSERT INTO `equipment_data` VALUES (/*equipment_id*/106221, /*equipment_name*/'黑珍珠暗狱杖', /*description*/'是冥府的黑女王引导死者灵魂所使用\n的法杖。从黑色宝珠中吹出的冥界之\n风甚至能将禁咒或是神明的奇迹都回\n归至虚无。', /*original_equipment_id*/106221, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/0.0, /*magic_str*/190.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/29.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
