INSERT INTO `equipment_data` VALUES (/*equipment_id*/106251, /*equipment_name*/'白珍珠光天杖', /*description*/'是天上的白色女神为祝福生命诞生而\n赐予的神杖。宝珠里充斥着圣光，可\n治愈百病，甚至还能驱散恶鬼邪神的\n诅咒。', /*original_equipment_id*/106251, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/25, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/345.0, /*atk*/0.0, /*magic_str*/145.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
