INSERT INTO `equipment_data` VALUES (/*equipment_id*/106253, /*equipment_name*/'冰雪华晶之杖', /*description*/'以飞舞了很长时间而化成的巨大\n雪花结晶为轴心的魔法杖。蕴藏在\n法杖中的永恒加护会给予装备者\n如永久冻土般的强健力量。', /*original_equipment_id*/106253, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/25, /*craft_flg*/1, /*equipment_enhance_point*/1126, /*sale_price*/36000, /*require_level*/124, /*hp*/250.0, /*atk*/0.0, /*magic_str*/142.0, /*def*/7.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106281, /*equipment_name*/'白金之煌鳞铠', /*description*/'将纯度极高的白金用圣火与灵峰的清水锻造而成的盔甲。连接在一起的\n白金片如同白龙的鳞片般闪闪发光，\n守护着穿戴之人。', /*original_equipment_id*/106281, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/520, /*sale_price*/34000, /*require_level*/117, /*hp*/0.0, /*atk*/78.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
