INSERT INTO `equipment_data` VALUES (/*equipment_id*/106312, /*equipment_name*/'玫瑰草莓护甲', /*description*/'布满了魔法草莓的铠甲，是点心之国\n的第一骑士穿戴过的铠甲。不仅是装\n备者，就连周围的人都会被它高雅的\n香气激发斗志。', /*original_equipment_id*/106312, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/31, /*craft_flg*/1, /*equipment_enhance_point*/1205, /*sale_price*/34000, /*require_level*/123, /*hp*/160.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/15.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
