INSERT INTO `equipment_data` VALUES (/*equipment_id*/106342, /*equipment_name*/'华丽马卡龙礼裙', /*description*/'用马卡龙制成的连衣裙，这种马卡龙\n蕴含了点心之国秘传的守护魔法。每\n一粒都包含了不同的魔力，能防御一\n切攻击。', /*original_equipment_id*/106342, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/34, /*craft_flg*/1, /*equipment_enhance_point*/980, /*sale_price*/34000, /*require_level*/123, /*hp*/0.0, /*atk*/109.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106343, /*equipment_name*/'薄红樱花圣衣', /*description*/'舞女们向春神献上感谢的舞蹈时所穿着的神圣服装。\n飘舞的樱花花瓣的装饰\n能提升装备者的身体能力。', /*original_equipment_id*/106343, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/34, /*craft_flg*/1, /*equipment_enhance_point*/980, /*sale_price*/36000, /*require_level*/129, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/6.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106371, /*equipment_name*/'黑玛瑙之祈装衣', /*description*/'从月光都照不进的地底深处挖出来的\n镶满黑玛瑙的黑衣。四处弥漫的漆黑\n取代了耀眼的光芒，让穿戴者在任何\n危难中都能隐藏身形。', /*original_equipment_id*/106371, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/520, /*sale_price*/34000, /*require_level*/117, /*hp*/0.0, /*atk*/0.0, /*magic_str*/87.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106372, /*equipment_name*/'魔法松饼长袍', /*description*/'在点心之国举办祭神仪式时使用的\n巨大松饼法衣。魔法黄油\n所散发出的柔和香味具有除魔的\n效果，能保护装备者免于灾厄。', /*original_equipment_id*/106372, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/1080, /*sale_price*/34000, /*require_level*/123, /*hp*/0.0, /*atk*/0.0, /*magic_str*/49.0, /*def*/10.0, /*magic_def*/14.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106373, /*equipment_name*/'白雪樱魔装', /*description*/'一件特别的长袍，上面的刺绣图案\n是在冬天绽放的传说中的樱花花瓣。这件长袍整体都是\n花瓣所组成的魔法阵，给予装备者\n源源不断的魔力。', /*original_equipment_id*/106373, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/1080, /*sale_price*/36000, /*require_level*/129, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/6.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106401, /*equipment_name*/'堇青石圆盾', /*description*/'用巨大的堇青石结晶制作而成的\n圆盾。其硬度能在任何困境中从物理\n到魔法层面保护装备者。', /*original_equipment_id*/106401, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/40, /*craft_flg*/1, /*equipment_enhance_point*/720, /*sale_price*/34000, /*require_level*/117, /*hp*/0.0, /*atk*/37.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106431, /*equipment_name*/'薄荷巧克力冰淇淋高跟鞋', /*description*/'用流传于点心之国永不融化的冰块\n为素材制作而成的靴子。据说，带有\n蓝色的特殊色调会让见到它的人为之\n倾倒，它甚至拥有魅惑神明的力量。', /*original_equipment_id*/106431, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/43, /*craft_flg*/1, /*equipment_enhance_point*/1200, /*sale_price*/34000, /*require_level*/123, /*hp*/0.0, /*atk*/24.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/18.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106461, /*equipment_name*/'月光水晶高跟鞋', /*description*/'这是妖精为女神量身定做的鞋。\n使用的材料是持续沐浴在月光之下的\n水晶，自身就能散发光芒。就像是和\n月光共舞一般，以此来避开灾厄。', /*original_equipment_id*/106461, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/46, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/117, /*hp*/125.0, /*atk*/0.0, /*magic_str*/43.0, /*def*/8.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106462, /*equipment_name*/'樱吹雪绑带凉鞋', /*description*/'即便跨越悠久的时光也仍未枯萎的樱花树——\n以它为原料制成的凉鞋。将装备者\n和大地之间建立魔法连结，\n使其生命力得到飞跃性的提高。', /*original_equipment_id*/106462, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/46, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/36000, /*require_level*/129, /*hp*/175.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/9.0, /*magic_def*/13.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106491, /*equipment_name*/'不散樱宽檐帽', /*description*/'用樱花编织的宽檐帽。传闻这种樱花\n盛开于四季常春的妖精乡，永不凋落\n给予装备者妖精王的加护，\n使出目不暇接的一击。', /*original_equipment_id*/106491, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/49, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/36000, /*require_level*/129, /*hp*/0.0, /*atk*/31.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/19.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106521, /*equipment_name*/'水果挞贝雷帽', /*description*/'用水果挞制作而成的帽子，这是在点\n心之国的祭典中使用的魔法水果塔。\n刚刚烤好的香味飘散在四周，能提升\n创造力，拥有急速提升魔力的力量。', /*original_equipment_id*/106521, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/52, /*craft_flg*/1, /*equipment_enhance_point*/985, /*sale_price*/34000, /*require_level*/123, /*hp*/0.0, /*atk*/0.0, /*magic_str*/122.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106551, /*equipment_name*/'神红戒红玉玫瑰', /*description*/'以掌管热情的红玉玫瑰为装饰的\n戒指。寄宿着战神之力，能赐予穿戴\n者横扫千军万马的绝对力量，以及连\n邪神的恐怖都能驱除的勇气。', /*original_equipment_id*/106551, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/114, /*hp*/95.0, /*atk*/124.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/40.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106552, /*equipment_name*/'爆米花项链', /*description*/'点心国王家传的项链，\n妆点着如宝石般闪闪发光的爆米花。\n每一粒爆米花所释放出的香味都能燃\n起装备者的食欲和斗争的本能。', /*original_equipment_id*/106552, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/120, /*hp*/100.0, /*atk*/137.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/41.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106553, /*equipment_name*/'狱天耳环', /*description*/'将几乎能让万物枯死的炎热封印其中\n的耳饰。以永远渴望胜利\n为代价，会给予装备者\n毁灭一切的力量。', /*original_equipment_id*/106553, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/36000, /*require_level*/126, /*hp*/90.0, /*atk*/150.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/44.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106581, /*equipment_name*/'永恒绿戒', /*description*/'以古老神树之园的绿叶为原型的戒\n指。翡翠的光辉就像永恒的绿叶一\n样，能庇佑佩戴者在无尽的旅途中获\n得幸运，还能赋予强健加护。', /*original_equipment_id*/106581, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/114, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/15.0, /*magic_def*/15.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/95.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106582, /*equipment_name*/'马卡龙坠饰', /*description*/'用马卡龙搭配西班牙油条的项链。\n马卡龙的做法在点心之国已经失传。无论是怎样的冲击，都能牢牢地保护好佩戴者。', /*original_equipment_id*/106582, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/120, /*hp*/35.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/17.0, /*magic_def*/16.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106583, /*equipment_name*/'旋风耳环', /*description*/'将旋风的舞动形态原封不动地封印其中的耳饰。可以一直在装备者的周围\n张开风之防护罩，使得剑和魔法\n都无法近身。', /*original_equipment_id*/106583, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/36000, /*require_level*/126, /*hp*/110.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/17.0, /*magic_def*/17.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106611, /*equipment_name*/'白圣石钻石之星', /*description*/'作为光之精灵的盟约之证而授予的金\n刚石戒指。据说，其无尽的光辉带来\n了取之不尽的魔力，蕴藏着可以创造\n一颗新星球的力量。', /*original_equipment_id*/106611, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/114, /*hp*/0.0, /*atk*/0.0, /*magic_str*/132.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/24.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106612, /*equipment_name*/'甜甜圈项圈', /*description*/'由点心之国传说中的工匠\n所制作的甜甜圈项链。香甜的气味\n能提升装备者的集中力，让魔法威力\n得到飞跃般的提升。', /*original_equipment_id*/106612, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/34000, /*require_level*/120, /*hp*/0.0, /*atk*/0.0, /*magic_str*/134.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/35.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106613, /*equipment_name*/'惠雨耳环', /*description*/'将滋润万物的温柔雨丝封印其中的耳饰。温柔落下的细雨\n能持续不断地治愈\n因战斗而受伤的装备者。', /*original_equipment_id*/106613, /*promotion_level*/6, /*equipment_type*/0, /*equipment_category*/61, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/36000, /*require_level*/126, /*hp*/0.0, /*atk*/0.0, /*magic_str*/140.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/36.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107011, /*equipment_name*/'樱花盛开', /*description*/'用倾注了春日气息的特殊矿石\n炼成的大剑。樱花图案能激发\n装备者的生命力，并赋予\n斩断任何物体的力量。', /*original_equipment_id*/107011, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/38000, /*require_level*/130, /*hp*/40.0, /*atk*/268.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/18.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107012, /*equipment_name*/'裸海蝶之剑', /*description*/'大剑的护手做成了被称为冰海天使的\n裸海蝶的样子。于冰冷汪洋中飞舞的\n翅膀加护，能赋予装备者不输给严酷\n环境的生命力。', /*original_equipment_id*/107012, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/1000, /*sale_price*/38000, /*require_level*/136, /*hp*/85.0, /*atk*/281.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107013, /*equipment_name*/'防暴钉剑', /*description*/'以在古战场上连战连胜而著称的战士\n曾经配备的剑。为了在乱战中取胜，\n比起锋利度更注重于打击力度，\n这把暴虐的利剑可以击飞任何敌人。', /*original_equipment_id*/107013, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/170.0, /*atk*/286.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107041, /*equipment_name*/'宵刀影樱', /*description*/'刀刃上散落着特殊樱花花瓣，\n传说是献给暗夜精灵之物。\n刀身能吸收光线，可隐藏斩击轨迹，\n发出必杀的一击。', /*original_equipment_id*/107041, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/1180, /*sale_price*/38000, /*require_level*/130, /*hp*/0.0, /*atk*/260.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/42.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107042, /*equipment_name*/'虎鲸之刃', /*description*/'以被称为海上暴徒的虎鲸的锐利背鳍\n为原型的太刀。使用这把刀，\n能如同在海面畅游的虎鲸一般\n自由自在地斩断敌人。', /*original_equipment_id*/107042, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/136, /*hp*/0.0, /*atk*/258.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/31.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107043, /*equipment_name*/'愤怒锁链之刃', /*description*/'在古战场上众所周知的狂剑士\n所挥舞的刀。与刀身一同挥动的锁链\n让斩击带来的疼痛成倍增长，\n在敌人身上刻下无尽的痛苦。', /*original_equipment_id*/107043, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/1120, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/297.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/31.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107071, /*equipment_name*/'樱花花瓣短剑', /*description*/'使用秘技将凋零之际的脆弱樱花花瓣\n保留在刀身上，有着锋利刀刃的短剑\n刀刃无比轻薄，\n可以割裂一切缝隙。
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107072, /*equipment_name*/'鲨鱼短剑', /*description*/'以嗜血的大海猎手——鲨鱼的尖牙\n为原型的短剑。据说它的刀刃\n一旦咬紧便绝不松口，\n能顺利将猎物逼入绝境。', /*original_equipment_id*/107072, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/136, /*hp*/0.0, /*atk*/278.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/41.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107073, /*equipment_name*/'雷击短剑', /*description*/'在古战场上被称为速度第一的战士\n曾经灵巧使用过的短剑。刀刃上迸发\n的雷电大幅提升了锐利度，\n拥有一击便能斩断敌人的超常威力。', /*original_equipment_id*/107073, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/295.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/35.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107101, /*equipment_name*/'樱花护手', /*description*/'绝世名匠使用传说中树龄超千年的\n樱花古木，仅用一把刀雕成的护手。\n古木静谧的生命力\n催生了必杀的一击。', /*original_equipment_id*/107101, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/38000, /*require_level*/130, /*hp*/120.0, /*atk*/248.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107102, /*equipment_name*/'海龟护手', /*description*/'以在海中畅游的海龟的龟壳为原型的\n护手。以绿宝石一般澄澈的光芒著称\n拥有毋庸置疑的硬度，\n可以粉碎任何物体。', /*original_equipment_id*/107102, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/38000, /*require_level*/136, /*hp*/0.0, /*atk*/239.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107103, /*equipment_name*/'猫爪指虎', /*description*/'据说在古战场上大显身手的兽人战士\n爱用的拳头兵器。制作成了专用于\n肉搏战的形状，可以发挥出猫一般的\n灵敏和肉食动物一般的蛮力。', /*original_equipment_id*/107103, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/10, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/312.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107131, /*equipment_name*/'垂枝樱花弓', /*description*/'用盛开在秘境的樱花古木的树枝\n制作的弓。选用自然形成的弯曲枝干\n制作而成，据说装备者可以\n与自然同化，获得各种加护。', /*original_equipment_id*/107131, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/1040, /*sale_price*/38000, /*require_level*/130, /*hp*/0.0, /*atk*/231.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107132, /*equipment_name*/'小丑鱼弓', /*description*/'以群居在盛夏海里的小丑鱼为原型的\n弓。据说装备者可以灵活利用地形\n神出鬼没地射箭，一边隐匿身形\n一边出其不意地射击敌人。', /*original_equipment_id*/107132, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/136, /*hp*/0.0, /*atk*/256.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/7.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107133, /*equipment_name*/'猎罪捕手弓', /*description*/'古战场以过人臂力令人畏惧的弓箭手\n曾经熟练使用的强弓。一般人甚至\n无法拉开弓弦，其释放出的利箭\n必然能一击射穿猎物的要害部位。', /*original_equipment_id*/107133, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/13, /*craft_flg*/1, /*equipment_enhance_point*/980, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/264.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/6.0, /*physical_critical*/17.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107161, /*equipment_name*/'散樱飒枪', /*description*/'将吹散樱花的风与飘落的花瓣\n一起封存的长枪。总是飞舞在\n装备者周围的花瓣会形成魔法护壁，\n能够防御带有恶意的魔法。', /*original_equipment_id*/107161, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/38000, /*require_level*/130, /*hp*/0.0, /*atk*/221.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107162, /*equipment_name*/'蟹钳之矛', /*description*/'以同时兼备锐利与牢固的蟹钳\n为原型的矛。坚韧度自不用说，\n分叉的特殊形状可以封住敌人的攻击\n以铁壁的防御而著称。', /*original_equipment_id*/107162, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/38000, /*require_level*/136, /*hp*/0.0, /*atk*/229.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107163, /*equipment_name*/'骷髅头哥特式长矛', /*description*/'传说在古战场上拥有不死身躯的战士\n一直装备在身上的长矛。矛头的饰品\n暗涌着强大的魔力，通过笼罩住\n装备者的全身来对其进行守护。', /*original_equipment_id*/107163, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/247.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107191, /*equipment_name*/'伐樱斧', /*description*/'被称为大英杰的人物曾经使用过的\n传说中的斧头。被这把斧头砍倒的\n樱花精灵非常钦佩它的锐利，因此\n没有施加惩罚，而是赠予了祝福。', /*original_equipment_id*/107191, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/1100, /*sale_price*/38000, /*require_level*/130, /*hp*/0.0, /*atk*/281.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107192, /*equipment_name*/'鲸鱼斧', /*description*/'以大海中自在畅游的鲸鱼喷水的身姿\n为原型的斧头。喷出的水之刃\n能赋予装备者横渡大海的惊异生命力\n和超凡的臂力。', /*original_equipment_id*/107192, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/136, /*hp*/255.0, /*atk*/270.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107193, /*equipment_name*/'摇滚斧头', /*description*/'总是在古战场上奔赴于最前线的战士\n曾经扛着的斧头。外形是以乐器为\n原型制作的，每次挥动响起的轰鸣声\n都能振奋装备者的身心。', /*original_equipment_id*/107193, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/19, /*craft_flg*/1, /*equipment_enhance_point*/980, /*sale_price*/38000, /*require_level*/142, /*hp*/240.0, /*atk*/273.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
