INSERT INTO `equipment_data` VALUES (/*equipment_id*/107223, /*equipment_name*/'挂锁权杖', /*description*/'曾被当成古战场上的破坏者，从而遭\n到忌讳和厌恶的魔术师的法杖。封印\n在其内部的强大魔力一旦被解放，\n整个战场将全部都吞没。', /*original_equipment_id*/107223, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/22, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/0.0, /*atk*/0.0, /*magic_str*/277.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/35.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
