INSERT INTO `equipment_data` VALUES (/*equipment_id*/107253, /*equipment_name*/'爆裂公主法杖', /*description*/'曾在古战场上被称为支配者的女帝\n所持有的法杖。法杖带有的加护\n能提升装备者的胆量，使其无论处于\n何种绝境，都能泰然自若地行动。', /*original_equipment_id*/107253, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/25, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/142, /*hp*/420.0, /*atk*/0.0, /*magic_str*/210.0, /*def*/4.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107281, /*equipment_name*/'夏日轻便比基尼铠甲', /*description*/'充分发挥在水边行动灵活的轻型铠甲\n远比外观看起来更适合战斗，\n大胆的设计和迅捷的行动相得益彰，\n不断释放出致命的一击。', /*original_equipment_id*/107281, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/960, /*sale_price*/38000, /*require_level*/135, /*hp*/0.0, /*atk*/140.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/8.0, /*physical_critical*/18.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
