INSERT INTO `equipment_data` VALUES (/*equipment_id*/107283, /*equipment_name*/'纯洁白雪铠甲', /*description*/'待最初的细雪聚积化为硬质结晶的雪\n后，将其细心打磨而成的铠甲。如圣\n水般清凉的雪之结晶以比金属更坚韧\n的硬度和更轻的重量来保护装备者。', /*original_equipment_id*/107283, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/28, /*craft_flg*/1, /*equipment_enhance_point*/1120, /*sale_price*/38000, /*require_level*/147, /*hp*/0.0, /*atk*/149.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/13.0, /*physical_critical*/22.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107311, /*equipment_name*/'沙滩泳圈铠甲', /*description*/'将保障戏水安全的游泳圈\n用特殊魔法强化后组装成的巨大铠甲\n同时兼顾刚度和弹性，因其非凡的\n冲击吸收能力而具有绝对的防御力。', /*original_equipment_id*/107311, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/31, /*craft_flg*/1, /*equipment_enhance_point*/1205, /*sale_price*/38000, /*require_level*/135, /*hp*/340.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/20.0, /*magic_def*/18.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107312, /*equipment_name*/'哥特式铁娘子', /*description*/'在古战场上守护君主的女骑士\n曾经穿过的铠甲。寄宿在铠甲内的\n忠义之心能赋予装备者钢铁般的强健\n以及绝不屈服的心。', /*original_equipment_id*/107312, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/31, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/141, /*hp*/430.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/22.0, /*magic_def*/20.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
