INSERT INTO `equipment_data` VALUES (/*equipment_id*/107343, /*equipment_name*/'白色极光礼服', /*description*/'将绽放于极点之夜空的微亮极光编入\n丝线，织造而成的礼服。每次飘动的\n时候，礼服所散发出的温暖光芒就会\n形成魔力层，守护装备者。', /*original_equipment_id*/107343, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/34, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/147, /*hp*/320.0, /*atk*/149.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107371, /*equipment_name*/'阳伞连衣裙', /*description*/'像夏日里随风而转的阳伞般能飘浮于\n空中的魔法连衣裙。\n适当的浮游速度不会妨碍咏唱，靠此\n能力可从敌人死角释放出必杀的一击', /*original_equipment_id*/107371, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/1080, /*sale_price*/38000, /*require_level*/135, /*hp*/0.0, /*atk*/0.0, /*magic_str*/167.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/17.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107372, /*equipment_name*/'哥特蓝玫瑰', /*description*/'在古战场上留下了传奇的年轻女宫廷\n魔导士所穿的礼裙。精致的缝制技术\n让其完美地兼顾了魔力效率与\n可爱的外观。', /*original_equipment_id*/107372, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/141, /*hp*/0.0, /*atk*/0.0, /*magic_str*/169.0, /*def*/6.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/20.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107373, /*equipment_name*/'钻石星尘长袍', /*description*/'将冻结的月夜空气织入其中的诅咒法\n衣。与金刚石同等硬度的细小冰晶形\n成了立体的天然魔法阵，在守护装备\n者的同时还能提升其魔力。', /*original_equipment_id*/107373, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/37, /*craft_flg*/1, /*equipment_enhance_point*/920, /*sale_price*/38000, /*require_level*/147, /*hp*/0.0, /*atk*/0.0, /*magic_str*/182.0, /*def*/6.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/18.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
