INSERT INTO `equipment_data` VALUES (/*equipment_id*/107463, /*equipment_name*/'霜雪护符高跟鞋', /*description*/'由极寒的冰海所孕育出的特殊珊瑚\n制作而成的鞋。设置在各处的\n雪之结晶护符能为装备者的动作提供\n辅助，减少体力的消耗。', /*original_equipment_id*/107463, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/46, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/147, /*hp*/460.0, /*atk*/0.0, /*magic_str*/83.0, /*def*/13.0, /*magic_def*/15.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
