INSERT INTO `equipment_data` VALUES (/*equipment_id*/107493, /*equipment_name*/'雪花贝雷帽', /*description*/'如同轻盈飘逸的雪花一般的帽子。\n帽子上附着有冰之结晶的加护，据说\n装备者即便身处极寒之地，\n也能获得无限活力，活动自如。', /*original_equipment_id*/107493, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/49, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/147, /*hp*/525.0, /*atk*/215.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107551, /*equipment_name*/'樱花飞舞的八重簪', /*description*/'将以绚烂绽放而著称的八重樱树枝的\n生命力直接封存的簪子。赋予装备者\n永远盛放的精力，无论任何情况\n都能让装备者发挥出最强的力量。', /*original_equipment_id*/107551, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/132, /*hp*/0.0, /*atk*/210.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107552, /*equipment_name*/'红宝石扶桑花发饰', /*description*/'移植了在南国盛开、洋溢着生命力的\n扶桑花的发饰。熠熠生辉的\n鲜红花瓣上充满了阳光的加护，\n能够赋予装备者强劲的活力。', /*original_equipment_id*/107552, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/138, /*hp*/90.0, /*atk*/225.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/11.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107553, /*equipment_name*/'爆裂尖刺手镯', /*description*/'手镯上寄宿着以强大力量席卷严酷战\n场的战士的灵魂。尖刺的装饰\n释放出的威力能给予装备者背水一战\n的气势和爆发性的攻击力。', /*original_equipment_id*/107553, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/55, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/144, /*hp*/0.0, /*atk*/247.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/18.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107581, /*equipment_name*/'樱花月夜簪', /*description*/'簪子里封存了洒在夜里静谧盛开的\n樱花树枝上的月光力量。月光的魔力\n和樱花的生命力会相互提升，并给予\n装备者无限的生命力。', /*original_equipment_id*/107581, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/132, /*hp*/315.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/19.0, /*magic_def*/19.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107582, /*equipment_name*/'珍珠椰子发饰', /*description*/'用收集的永葆生命力的椰子果实制作\n而成的发饰，散发出被大海所环抱的\n大珍珠的光芒。守护果实的果壳加护\n能保护装备者不受任何攻击的伤害。', /*original_equipment_id*/107582, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/138, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/24.0, /*magic_def*/24.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/107583, /*equipment_name*/'绿宝石哥特式手镯', /*description*/'手镯上寄宿着在严酷的战场上用牢固\n的防守提供支援的骑士之魂。上面密\n密麻麻地刻着肉眼看不见的魔法纹路\n能够保护装备者不受任何攻击的伤害', /*original_equipment_id*/107583, /*promotion_level*/7, /*equipment_type*/0, /*equipment_category*/58, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/38000, /*require_level*/144, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/26.0, /*magic_def*/25.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
