INSERT INTO `equipment_data` VALUES (/*equipment_id*/108011, /*equipment_name*/'冰辉剑古拉斯之刃', /*description*/'自神代以来从未融化过的永久冰块\n经过切削后，制成的冰之大剑。具有\n压倒性的光辉及硬度，用它进行攻击\n任何敌人都能一斩而断。', /*original_equipment_id*/108011, /*promotion_level*/8, /*equipment_type*/0, /*equipment_category*/1, /*craft_flg*/1, /*equipment_enhance_point*/500, /*sale_price*/40000, /*require_level*/148, /*hp*/100.0, /*atk*/330.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/18.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
