INSERT INTO `equipment_data` VALUES (/*equipment_id*/108042, /*equipment_name*/'创音刀歌尔多纳', /*description*/'号称最强的武奏乐团代代相传的弯刀\n每次挥刀的破风之音产生共鸣，\n使其成为能斩断万物的究极利刃，\n带领装备者走向胜利。', /*original_equipment_id*/108042, /*promotion_level*/8, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/900, /*sale_price*/40000, /*require_level*/154, /*hp*/40.0, /*atk*/356.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
