INSERT INTO `equipment_data` VALUES (/*equipment_id*/108044, /*equipment_name*/'残酷的蔷薇之刃', /*description*/'醉心于某帝国神话的工匠\n为了再现神器而制成的妖艳之刀。\n它会吸收亡敌的鲜血和生命，将刀身和\n蔷薇染上更加鲜艳的红色。', /*original_equipment_id*/108044, /*promotion_level*/8, /*equipment_type*/0, /*equipment_category*/4, /*craft_flg*/1, /*equipment_enhance_point*/900, /*sale_price*/40000, /*require_level*/166, /*hp*/50.0, /*atk*/399.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/108071, /*equipment_name*/'冰牙斯蒂里亚短剑', /*description*/'使用在灵峰断崖生成并存在了数百年\n的冰柱制成的短剑。饱经风雪的洗礼\n起伏的刀尖可精准捕捉到对手的弱点\n发出冰冷刺骨的一击。', /*original_equipment_id*/108071, /*promotion_level*/8, /*equipment_type*/0, /*equipment_category*/7, /*craft_flg*/1, /*equipment_enhance_point*/500, /*sale_price*/40000, /*require_level*/148, /*hp*/0.0, /*atk*/335.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
