INSERT INTO `equipment_data` VALUES (/*equipment_id*/108161, /*equipment_name*/'冰穿枪斯诺斯托姆', /*description*/'长枪的枪尖由经过上万年暴风雪\n切割而形成的尖冰制成。由大自然的\n威力所锻造的枪刃具有很高的杀伤力\n能将对手破坏到体无完肤。', /*original_equipment_id*/108161, /*promotion_level*/8, /*equipment_type*/0, /*equipment_category*/16, /*craft_flg*/1, /*equipment_enhance_point*/900, /*sale_price*/40000, /*require_level*/148, /*hp*/0.0, /*atk*/320.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
