INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24015, /*where_type*/24, /*character_id*/110501, /*face_type*/4, /*character_name*/'香织（夏日）', /*description*/'啊呜～真遗憾～\n是被奄美魔物\n夺去灵魂了吗？', /*voice_id*/1, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/4, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24016, /*where_type*/24, /*character_id*/110501, /*face_type*/4, /*character_name*/'香织（夏日）', /*description*/'今天的舞跳起来也没什么劲呢～\n吃些类似苦瓜的东西，打起精神来吧！', /*voice_id*/2, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.0, /*change_face_type*/1, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24017, /*where_type*/24, /*character_id*/110501, /*face_type*/4, /*character_name*/'香织（夏日）', /*description*/'到处都是奄美魔物，眼睛都花了～\n晕乎乎的～', /*voice_id*/3, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/4, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24018, /*where_type*/24, /*character_id*/110501, /*face_type*/1, /*character_name*/'香织（夏日）', /*description*/'总会好的、总会好的。\n下次加油就好！\n在海里游会儿泳，\n振作起来吧！', /*voice_id*/4, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/5.2, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24019, /*where_type*/24, /*character_id*/110501, /*face_type*/5, /*character_name*/'香织（夏日）', /*description*/'我这么丢人的样子\n都被你看到了～\n泳装？我不是指泳装哦？', /*voice_id*/5, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.2, /*change_face_type*/6, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24020, /*where_type*/24, /*character_id*/110501, /*face_type*/2, /*character_name*/'香织（夏日）', /*description*/'状态超棒！就这样\n让大海恢复和平吧！\n快乐的夏天近在眼前！', /*voice_id*/6, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24021, /*where_type*/24, /*character_id*/110501, /*face_type*/1, /*character_name*/'香织（夏日）', /*description*/'不管有几只来袭，\n我都不会被吓到哦！\n啊嚏！啊……\n不过好像被刚才的喷嚏吓了一跳～', /*voice_id*/7, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/6.4, /*change_face_type*/6, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24022, /*where_type*/24, /*character_id*/110501, /*face_type*/2, /*character_name*/'香织（夏日）', /*description*/'咿～呀～萨～萨♪\n在战斗结束后尽情跳舞，\n心情都舒畅起来了！', /*voice_id*/8, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24023, /*where_type*/24, /*character_id*/110501, /*face_type*/6, /*character_name*/'香织（夏日）', /*description*/'咦？在战斗的时候\n泳装不知道跑哪儿去了？\n我说，你有看到吗？\n为什么要远离我啊？', /*voice_id*/9, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/10.08, /*change_face_type*/3, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24024, /*where_type*/24, /*character_id*/110501, /*face_type*/1, /*character_name*/'香织（夏日）', /*description*/'我还很有精神呢！\n不管是奄美魔物还是别的，\n都尽管冲我来吧♪', /*voice_id*/10, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/5.0, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24025, /*where_type*/24, /*character_id*/110501, /*face_type*/2, /*character_name*/'香织（夏日）', /*description*/'我的空手道是世上最强的！\n再配上夏日的阳光与碧蓝的大海，\n可谓所向披靡了！', /*voice_id*/11, /*start_time*/'2019/07/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10021);
