INSERT INTO `login_bonus_detail` VALUES (/*id*/36, /*login_bonus_id*/40000, /*count*/1, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/100, /*character_id*/0, /*character_name*/'', /*description*/'', /*voice_id*/0, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/37, /*login_bonus_id*/40001, /*count*/1, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107501, /*character_name*/'佩可莉姆', /*description*/'今天是纪念日，所以要庆祝！\n烤肉、寿司、比萨、蛋糕～☆\n能和你一起享受派对，\n好幸福～♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/38, /*login_bonus_id*/40001, /*count*/2, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107601, /*character_name*/'可可萝', /*description*/'得益于主人的聪明才智，\n我们才能迎来这个纪念日\n真不愧是主人\n了不起，了不起♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/39, /*login_bonus_id*/40001, /*count*/3, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107801, /*character_name*/'凯露', /*description*/'你干得不错嘛！\n让我有点……稍微……还、还是\n对你刮目相看了\n表扬你一下♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/40, /*login_bonus_id*/40001, /*count*/4, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107701, /*character_name*/'铃莓', /*description*/'对于你我来说，\n今天是个特别的日子\n因为有你的支持，\n我才能努力下去！', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/41, /*login_bonus_id*/40001, /*count*/5, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107901, /*character_name*/'珠希', /*description*/'今天对你来说\n是特别的纪念日喵\n一直很感谢\n你的努力喵～♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/42, /*login_bonus_id*/40001, /*count*/6, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107501, /*character_name*/'佩可莉姆', /*description*/'呜～刨冰吃太多，\n身体好冷……\n用你的体温来温暖我吧！\n抱紧——♪', /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/43, /*login_bonus_id*/40001, /*count*/7, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107601, /*character_name*/'可可萝', /*description*/'都说夏季充满了危险……\n可是，我会好好\n守护主人的', /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/44, /*login_bonus_id*/40001, /*count*/8, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107801, /*character_name*/'凯露', /*description*/'嗯～为什么会变成要和你\n度过一整个夏天呢……\n不过，还挺开心的，就这样吧！\n走，接下来去哪儿玩呢！', /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/45, /*login_bonus_id*/40001, /*count*/9, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107701, /*character_name*/'铃莓', /*description*/'和你度过的第一个夏天……\n我现在好期待啊\n快乐又美好的回忆，\n又要增加一个了呢♪', /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/46, /*login_bonus_id*/40001, /*count*/10, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107901, /*character_name*/'珠希', /*description*/'本来夏天是最能赚钱的时期，\n但是今年我想和你一起度过喵\n你愿意和我一起\n增加快乐的回忆吗喵？', /*voice_id*/2, /*bg_id*/0);
