INSERT INTO `login_bonus_detail` VALUES (/*id*/76, /*login_bonus_id*/40000, /*count*/1, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/100, /*character_id*/0, /*character_name*/'', /*description*/'', /*voice_id*/0, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/77, /*login_bonus_id*/40001, /*count*/1, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107501, /*character_name*/'佩可莉姆', /*description*/'今天是纪念日，所以要庆祝！\n烤肉、寿司、比萨、蛋糕～☆\n能和你一起享受派对，\n好幸福～♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/78, /*login_bonus_id*/40001, /*count*/2, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107601, /*character_name*/'可可萝', /*description*/'得益于主人的聪明才智，\n我们才能迎来这个纪念日\n真不愧是主人\n了不起，了不起♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/79, /*login_bonus_id*/40001, /*count*/3, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107801, /*character_name*/'凯露', /*description*/'你干得不错嘛！\n让我有点……稍微……还、还是\n对你刮目相看了\n表扬你一下♪', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/80, /*login_bonus_id*/40001, /*count*/4, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107701, /*character_name*/'铃莓', /*description*/'对于你我来说，\n今天是个特别的日子\n因为有你的支持，\n我才能努力下去！', /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/81, /*login_bonus_id*/40001, /*count*/5, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107901, /*character_name*/'珠希', /*description*/'今天对你来说\n是特别的纪念日喵\n一直很感谢\n你的努力喵～♪', /*voice_id*/1, /*bg_id*/0);
