INSERT INTO `login_bonus_message_data` VALUES (/*id*/168, /*login_bonus_id*/80003, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'距离2.5周年还有10天……\n诶～\n居然还挺快呢？', /*voice_id*/18, /*additional_type*/0, /*additional_param*/'');
