INSERT INTO `login_bonus_message_data` VALUES (/*id*/170, /*login_bonus_id*/80003, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'干劲十足呢，可萝仔……\n这么拼命会很累的哦？\n放松一点吧？', /*voice_id*/19, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/171, /*login_bonus_id*/80003, /*type*/1, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'凯露小姐……\n谢谢您。\n凯露小姐真的\n非常温柔。', /*voice_id*/13, /*additional_type*/0, /*additional_param*/'');
