INSERT INTO `login_bonus_message_data` VALUES (/*id*/174, /*login_bonus_id*/80003, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'拜托我？真稀奇啊，\n可萝仔居然会说这种话。\n算了，只要是我能做到的事，\n倒也没关系。', /*voice_id*/21, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/175, /*login_bonus_id*/80003, /*type*/1, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'非常感谢。\n其实，我想为纪念日\n提前准备一种叫\n“即兴表演”的东西。', /*voice_id*/15, /*additional_type*/0, /*additional_param*/'');
