INSERT INTO `login_bonus_message_data` VALUES (/*id*/179, /*login_bonus_id*/80003, /*type*/0, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'还有9天！　马上就到了哦♪\n诶嘿嘿，太期待太期待了，\n吃饭都特别香呢☆', /*voice_id*/17, /*additional_type*/0, /*additional_param*/'');
