INSERT INTO `login_bonus_message_data` VALUES (/*id*/380, /*login_bonus_id*/80005, /*type*/0, /*day_count*/4, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'既然这样，只能想办法\n找到时间星盘，\n抹除刚才那段对话……\n诶，你怎么了，雪菲。', /*voice_id*/15, /*additional_type*/0, /*additional_param*/'');
