INSERT INTO `login_bonus_message_data` VALUES (/*id*/382, /*login_bonus_id*/80005, /*type*/3, /*day_count*/4, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'小雪菲！　之前的时光\n知道不知道都没关系哦！\n我们能够在一起的时间还有很多，\n只要留下更多的回忆就可以了！', /*voice_id*/12, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/383, /*login_bonus_id*/80005, /*type*/1, /*day_count*/4, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'是啊，现在这一刻，\n在未来也会成为\n无可替代的回忆吧。', /*voice_id*/12, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/384, /*login_bonus_id*/80005, /*type*/0, /*day_count*/4, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'所以别摆出这幅表情了。\n走吧，要继续为庆祝做准备了哦！', /*voice_id*/16, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/385, /*login_bonus_id*/80005, /*type*/2, /*day_count*/4, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'各位……确实呢。\n办一场最棒的庆典，\n留下最美好的回忆吧！', /*voice_id*/12, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/386, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'距离3.5周年还有10天！\n很快就要到那一天了呢！\n太让人兴奋了，超棒的对吧！', /*voice_id*/13, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/387, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'对了……之前发生过有很多玩具\n袭击孩子们的\n骚动呢……\n一大堆玩具都自己动起来了！', /*voice_id*/14, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/388, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'听说好像有很多小玩具\n闯入学校和孤儿院，\n大闹了一番呢！', /*voice_id*/15, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/389, /*login_bonus_id*/80005, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'说是大闹，\n那些也不过只是玩具啊。\n应该都很小巧吧？', /*voice_id*/17, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/390, /*login_bonus_id*/80005, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'既然那样，\n用魔法全部轰飞不就可以了！\n没什么大不了的啦！', /*voice_id*/18, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/391, /*login_bonus_id*/80005, /*type*/2, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'凯露……\n那可是孩子们\n最珍视的东西哦？', /*voice_id*/13, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/392, /*login_bonus_id*/80005, /*type*/1, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'这么粗暴的话\n是不能随便说出口的哦？\n会对孩子的教育\n造成负面影响的……', /*voice_id*/13, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/393, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'小凯露不也是\n出人意外地收集了很多\n猫咪周边吗？\n如果它们突然四处大闹了呢？', /*voice_id*/16, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/394, /*login_bonus_id*/80005, /*type*/0, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'你、你好啰嗦啊！\n别说什么出人意料！\n不、不过，那样确实\n做不到直接用魔法轰飞它们呢……', /*voice_id*/19, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/395, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'啊哈哈☆\n小凯露这样的个性\n我很喜欢哦♪', /*voice_id*/17, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/396, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'那我就给小凯露\n送上猫咪周边吧！\n这是在一部分人当中很受欢迎的\n鲷鱼烧店的附赠模型哦♪', /*voice_id*/18, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/397, /*login_bonus_id*/80005, /*type*/3, /*day_count*/5, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'请不要粗暴地对待它，\n不然它可能会\n突然大闹哦！', /*voice_id*/19, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/398, /*login_bonus_id*/80005, /*type*/2, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/190801, /*character_name*/'花凛', /*message*/'距离3.5周年还有9天。\n现在给大家带来一条\n特大新闻！', /*voice_id*/4, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/399, /*login_bonus_id*/80005, /*type*/2, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/190801, /*character_name*/'花凛', /*message*/'庆贺3.5周年的直播节目\n和去年一样。\n【美食殿堂】的各位将会\n一起出演！', /*voice_id*/5, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/400, /*login_bonus_id*/80005, /*type*/1, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'什么！？', /*voice_id*/20, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/401, /*login_bonus_id*/80005, /*type*/3, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'真的吗！？\n那么现在就得打起精神，\n做直播节目时吃的魔物料理\n才行呢☆', /*voice_id*/20, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/402, /*login_bonus_id*/80005, /*type*/0, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'等下……要努力的\n不是这个方面吧……\n话说，各位去年也出演了啊。\n好厉害！', /*voice_id*/14, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/403, /*login_bonus_id*/80005, /*type*/4, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'是的。虽然很紧张\n但那是富有意义的重大任务。\n我们一起努力吧，\n雪菲小姐。', /*voice_id*/14, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/404, /*login_bonus_id*/80005, /*type*/0, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'我、我也要出演吗！？　但是……\n我也是【美食殿堂】的一员呢。\n那我现在就得好好准备，\n调整状态了……', /*voice_id*/15, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/405, /*login_bonus_id*/80005, /*type*/1, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'哼哼♪\n真期待直播开始前，\n雪菲紧张得浑身僵硬的样子呢！', /*voice_id*/21, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/406, /*login_bonus_id*/80005, /*type*/3, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'去年的小凯露\n紧张得一直在发抖，\n非常可爱呢！\n今年我还想再看看那副模样！', /*voice_id*/21, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/407, /*login_bonus_id*/80005, /*type*/1, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'小心我宰了你哦！？', /*voice_id*/22, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/408, /*login_bonus_id*/80005, /*type*/2, /*day_count*/6, /*luck_pattern*/0, /*rate*/0, /*character_id*/190801, /*character_name*/'花凛', /*message*/'就是这样\n敬请期待向大家传达“公主连结！\nRe:Dive”魅力的\n直播节目♪', /*voice_id*/6, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/409, /*login_bonus_id*/80005, /*type*/1, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'距离3.5周年还有8天。\n真让人期待呢，\n主人。', /*voice_id*/15, /*additional_type*/0, /*additional_param*/'');
