INSERT INTO `login_bonus_message_data` VALUES (/*id*/412, /*login_bonus_id*/80005, /*type*/3, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'咦？　这么说来，\n前不久在月光学院，\n好像发生了一件事情……', /*voice_id*/22, /*additional_type*/0, /*additional_param*/'');
