INSERT INTO `login_bonus_message_data` VALUES (/*id*/414, /*login_bonus_id*/80005, /*type*/2, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'闹着\n要求不让暑假结束的事情吗？\n那是真的吗？\n我还以为是都市传说呢。', /*voice_id*/17, /*additional_type*/0, /*additional_param*/'');
